--
-- Cấu trúc bảng cho bảng `products`
--

CREATE TABLE `products` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `name` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL,
  `desc` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `content` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `price` int(11) NOT NULL,
  `thumbnail` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL,
  `qty` int(11) NOT NULL,
  `status` enum('1','2') COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `cat_id` bigint(20) UNSIGNED DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `products`
--

INSERT INTO `products` (`id`, `name`, `desc`, `content`, `price`, `thumbnail`, `qty`, `status`, `created_at`, `updated_at`, `cat_id`, `deleted_at`) VALUES
(6, 'iPhone Samsung Galaxy Z Fold2 5G', '<h3><a title=\"Tham khảo gi&aacute; điện thoại m&agrave;n h&igrave;nh gập Samsung Galaxy Z Fold 2 ch&iacute;nh h&atilde;ng tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-fold-2\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; điện thoại m&agrave;n h&igrave;nh gập Samsung Galaxy Z Fold 2 ch&iacute;nh h&atilde;ng tại Thegioididong.com\">Samsung Galaxy Z Fold 2</a>&nbsp;l&agrave; t&ecirc;n gọi ch&iacute;nh thức của&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại, smartphone ch&iacute;nh h&atilde;ng, nhiều khuyến m&atilde;i tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; điện thoại, smartphone ch&iacute;nh h&atilde;ng, nhiều khuyến m&atilde;i tại Thegioididong.com\">điện thoại</a>&nbsp;m&agrave;n h&igrave;nh gập cao cấp nhất của&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại, smartphone Samsung ch&iacute;nh h&atilde;ng, gi&aacute; tốt tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; điện thoại, smartphone Samsung ch&iacute;nh h&atilde;ng, gi&aacute; tốt tại Thegioididong.com\">Samsung</a>.&nbsp;Với nhiều n&acirc;ng cấp ti&ecirc;n phong về thiết kế, hiệu năng v&agrave; camera, hứa hẹn đ&acirc;y sẽ l&agrave; một si&ecirc;u phẩm th&agrave;nh c&ocirc;ng tiếp theo đến từ &ldquo;&ocirc;ng tr&ugrave;m&rdquo; sản xuất điện thoại lớn nhất thế giới.&nbsp;</h3>', '<h3>Thiết kế cải tiến hướng đến sự ho&agrave;n thiện</h3>\r\n<p>Samsung Galaxy Z Fold 2 vẫn giữ nguy&ecirc;n cơ chế m&agrave;n h&igrave;nh gập ở thế hệ tiền nhiệm. Như một quyển s&aacute;ch, chiếc điện thoại mở ra để hiển thị m&agrave;n h&igrave;nh lớn b&ecirc;n trong. B&ecirc;n ngo&agrave;i l&agrave; một m&agrave;n h&igrave;nh phụ&nbsp;<a title=\"Tham khảo c&aacute;c mẫu điện thoại m&agrave;n h&igrave;nh tr&agrave;n viền tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-man-hinh-tran-vien\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c mẫu điện thoại m&agrave;n h&igrave;nh tr&agrave;n viền tại Thegioididong.com\">tr&agrave;n viền</a>&nbsp;với thiết kế đục lỗ.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/226099/samsung-galaxy-z-fold-2-302920-012935.jpg\"><img class=\"lazy\" title=\"M&agrave;n h&igrave;nh gập cao cấp nhất | Galaxy Z Fold 2\" src=\"https://cdn.tgdd.vn/Products/Images/42/226099/samsung-galaxy-z-fold-2-302920-012935.jpg\" alt=\"M&agrave;n h&igrave;nh gập cao cấp nhất | Galaxy Z Fold 2\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/226099/samsung-galaxy-z-fold-2-302920-012935.jpg\" /></a></p>\r\n<p>Những chất liệu cao cấp nhất đ&atilde; được Samsung ưu &aacute;i trang bị l&ecirc;n Galaxy Z Fold 2, khung m&aacute;y được l&agrave;m từ nh&ocirc;m, vu&ocirc;ng vức hơn, gi&uacute;p cho si&ecirc;u phẩm m&agrave;n h&igrave;nh gập mới của Samsung c&oacute; một diện mạo nam t&iacute;nh v&agrave; sang trọng hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/226099/samsung-galaxy-z-fold-2-302920-012942.jpg\"><img class=\"lazy\" title=\"Thiết kế cải tiến, sang trọng hơn | Galaxy Z Fold 2\" src=\"https://cdn.tgdd.vn/Products/Images/42/226099/samsung-galaxy-z-fold-2-302920-012942.jpg\" alt=\"Thiết kế cải tiến, sang trọng hơn | Galaxy Z Fold 2\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/226099/samsung-galaxy-z-fold-2-302920-012942.jpg\" /></a></p>\r\n<p>Mặt lưng của thiết bị được ho&agrave;n thiện từ k&iacute;nh cao cấp, c&oacute; độ b&oacute;ng mờ gi&uacute;p giảm để lại dấu v&acirc;n tay. B&ecirc;n cạnh m&agrave;u đen truyền thống, m&agrave;u v&agrave;ng đồng - gam m&agrave;u mới lần đầu được Samsung giới thiệu tr&ecirc;n Galaxy Z Fold 2 cũng rất ấn tượng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/226099/samsung-galaxy-z-fold-2-302920-012949.jpg\"><img class=\"lazy\" title=\"Gam m&agrave;u v&agrave;ng đồng mới ấn tượng | Galaxy Z Fold 2\" src=\"https://cdn.tgdd.vn/Products/Images/42/226099/samsung-galaxy-z-fold-2-302920-012949.jpg\" alt=\"Gam m&agrave;u v&agrave;ng đồng mới ấn tượng | Galaxy Z Fold 2\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/226099/samsung-galaxy-z-fold-2-302920-012949.jpg\" /></a></p>\r\n<p>Đ&aacute;ng ch&uacute; &yacute; l&agrave; phần bản lề tr&ecirc;n Galaxy Z Fold 2 đ&atilde; được Samsung cải tiến gi&uacute;p cho thao t&aacute;c gập mở m&aacute;y trở n&ecirc;n sắc sảo. Giờ đ&acirc;y người d&ugrave;ng c&oacute; thể mở v&agrave; sử dụng m&aacute;y linh hoạt ở nhiều g&oacute;c độ kh&aacute;c nhau tương tự như mẫu smartphone&nbsp;<a title=\"Tham khảo gi&aacute; b&aacute;n điện thoại Samsung Galaxy Z Flip ch&iacute;nh h&atilde;ng tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-flip\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; b&aacute;n điện thoại Samsung Galaxy Z Flip ch&iacute;nh h&atilde;ng tại Thegioididong.com\">Samsung Galaxy Z Flip</a>.</p>\r\n<p>Với độ mỏng chỉ 6.9 mm khi mở ra, gập lại th&igrave; m&aacute;y lại c&agrave;ng gọn g&agrave;ng hơn, gi&uacute;p cho việc sử dụng bằng 1 tay tr&ecirc;n m&agrave;n h&igrave;nh phụ trở n&ecirc;n tối ưu hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/226099/samsung-galaxy-z-fold-2-305720-025722.jpg\"><img class=\"lazy\" title=\"Thuận tiện cho sử dụng một tay | Galaxy Z Fold 2\" src=\"https://cdn.tgdd.vn/Products/Images/42/226099/samsung-galaxy-z-fold-2-305720-025722.jpg\" alt=\"Thuận tiện cho sử dụng một tay | Galaxy Z Fold 2\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/226099/samsung-galaxy-z-fold-2-305720-025722.jpg\" /></a></p>\r\n<p>Cạnh dưới của m&aacute;y l&agrave; loa ngo&agrave;i, mic thoại v&agrave; một cổng sạc. Thật đ&aacute;ng tiếc khi Samsung tiếp tục loại bỏ jack 3.5 mm tr&ecirc;n những mẫu điện thoại cao cấp của m&igrave;nh.</p>', 46000000, 'uploads/products/samsung-galaxy-z-fold-2-vang-dong-600x600.jpg', 45, '2', '2021-08-31 17:00:00', '2021-10-18 03:11:22', 1, NULL),
(7, 'iPhone Samsung Galaxy Z Fold2 5G Đặc Biệt', '<h3>Thuộc d&ograve;ng smartphone cao cấp,&nbsp;<a title=\"Tham khảo điện thoại Samsung Galaxy Z Fold2 5G kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-fold2-5g-dac-biet\" target=\"_blank\" rel=\"noopener\">Samsung Galaxy Z Fold2 5G</a>&nbsp;được&nbsp;<a title=\"Tham khảo điện thoại Samsung kinh doanh tại thegioididong.com\" href=\"https://thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\">Samsung</a>&nbsp;trau chuốt kh&ocirc;ng chỉ vẻ ngo&agrave;i sang trọng, tinh tế m&agrave; lẫn cả &ldquo;nội thất&rdquo; b&ecirc;n trong đầy mạnh mẽ khiến chiếc&nbsp;<a title=\"Tham khảo smartphone kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">smartphone</a>&nbsp;n&agrave;y ho&agrave;n to&agrave;n xứng đ&aacute;ng để được sở hữu.</h3>', '<h3>Bứt ph&aacute; ngoạn mục với thiết kế thời thượng</h3>\r\n<p>Phần &ldquo;xương sống&rdquo; của m&aacute;y cho ph&eacute;p bạn gập mở nhịp nh&agrave;ng ở nhiều g&oacute;c độ kh&aacute;c nhau, mang đến cảm gi&aacute;c chắc chắn, mượt m&agrave; hơn, kh&ocirc;ng giống như thế hệ Galaxy Fold đầu ti&ecirc;n.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282120-112134.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Điện thoại gập mở 2 trong 1\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282120-112134.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Điện thoại gập mở 2 trong 1\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282120-112134.jpg\" /></a></p>\r\n<p>Khi gập lại, m&aacute;y cho độ d&agrave;y 16.8 mm được thu hẹp đ&aacute;ng kể hơn trước, trong khi mở ra ho&agrave;n to&agrave;n th&igrave; mỏng 6.9 mm.</p>\r\n<p>T&iacute;nh năng&nbsp;<a title=\"Tham khảo điện thoại c&oacute; cảm biến v&acirc;n tay kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-bao-mat-van-tay\" target=\"_blank\" rel=\"noopener\">bảo mật v&acirc;n tay</a>&nbsp;của Samsung Galaxy Z Fold2 5G được t&iacute;ch hợp v&agrave;o n&uacute;t nguồn, đặt b&ecirc;n cạnh phải của m&aacute;y, chỉ cần một lần chạm l&agrave; c&oacute; thể mở kh&oacute;a m&aacute;y nhanh ch&oacute;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254520-094512.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Cảm biến v&acirc;n tay đặt ở cạnh b&ecirc;n\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254520-094512.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Cảm biến v&acirc;n tay đặt ở cạnh b&ecirc;n\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254520-094512.jpg\" /></a></p>\r\n<p>Ngo&agrave;i ra, mặt sau của m&aacute;y được l&agrave;m bằng k&iacute;nh cường lực Gorilla Glass 6, bảo vệ thiết bị khỏi va đập trong cuộc sống h&agrave;ng ng&agrave;y tốt hơn.</p>\r\n<h3>Trải nghiệm m&agrave;n h&igrave;nh đỉnh cao</h3>\r\n<p>Samsung Galaxy Z Fold2 5G với m&agrave;n h&igrave;nh ngo&agrave;i sử dụng tấm nền Super AMOLED 6.23 inch được bảo vệ bằng k&iacute;nh cường lực Gorilla Glass Victus, được cho l&agrave; bền bỉ v&agrave; chống xước vượt trội hơn Gorilla Glass 6.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254320-094345.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Thiết kế hướng đến sự ho&agrave;n thiện\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254320-094345.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Thiết kế hướng đến sự ho&agrave;n thiện\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254320-094345.jpg\" /></a></p>\r\n<p>M&agrave;n h&igrave;nh b&ecirc;n trong chắc chắn sẽ thu h&uacute;t nhiều sự ch&uacute; &yacute; hơn khi Samsung sử dụng một loại k&iacute;nh si&ecirc;u mỏng kh&ocirc;ng chỉ c&oacute; khả năng chịu lực tuyệt vời m&agrave; c&ograve;n giảm thiểu tối đa &aacute;nh s&aacute;ng xanh, cho ph&eacute;p bạn trải nghiệm nhiều giờ liền m&agrave; kh&ocirc;ng gặp phải t&igrave;nh trạng nhức mỏi mắt.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254420-094400.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - M&agrave;n h&igrave;nh lớn v&agrave; bền\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254420-094400.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - M&agrave;n h&igrave;nh lớn v&agrave; bền\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254420-094400.jpg\" /></a></p>\r\n<p>M&agrave;n h&igrave;nh b&ecirc;n trong của Samsung Galaxy Z Fold2 5G được trang bị tấm nền Dynamic AMOLED c&oacute; độ ph&acirc;n giải Full HD+ với k&iacute;ch thước 7.59 inch cho ph&eacute;p bạn trải nghiệm tr&ograve; chơi, xem phim v&agrave; c&aacute;c hoạt động giải tr&iacute; kh&aacute;c với khả năng hiển thị r&otilde; n&eacute;t c&ugrave;ng c&aacute;c dải m&agrave;u rực rỡ.</p>\r\n<p>Đ&aacute;ng ch&uacute; &yacute; nhất ch&iacute;nh l&agrave; tốc độ l&agrave;m mới m&agrave;n h&igrave;nh 120 Hz cho khả năng xử l&yacute; mượt m&agrave;, liền mạch v&agrave; tốc độ phản hồi nhanh ấn tượng.</p>\r\n<h3>Hiệu năng vượt ngo&agrave;i mong đợi</h3>\r\n<p>Z Fold2 5G sở hữu bộ xử l&yacute; Snapdragon 865+ ti&ecirc;n tiến gồm 8 l&otilde;i, trong đ&oacute; 1 l&otilde;i hoạt động ở tốc độ l&ecirc;n đến 3.09 Ghz, 3 l&otilde;i với tần số xung nhịp l&agrave; 2.4 Ghz v&agrave; 4 l&otilde;i đạt 1.8 Ghz.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-285120-105117.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Chip Snapdragon 865+\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-285120-105117.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Chip Snapdragon 865+\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-285120-105117.jpg\" /></a></p>\r\n<p>Phần đồ họa l&agrave; GPU Adreno 650 cho hiệu suất tốt khi chơi game, nhờ thời gian phản hồi nhanh, độ trễ được cải thiện vượt trội v&agrave; m&agrave;n h&igrave;nh ch&iacute;nh với tần số qu&eacute;t 120 Hz cho chuyển động liền mạch mượt m&agrave; ấn tượng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254520-094532.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Cấu h&igrave;nh khủng, chiến mọi game\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254520-094532.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Cấu h&igrave;nh khủng, chiến mọi game\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254520-094532.jpg\" /></a></p>\r\n<p>Hơn thế nữa, Samsung Galaxy Z Fold2 5G cung cấp kh&ocirc;ng gian lưu trữ lớn, trong đ&oacute;&nbsp;<a title=\"Tham khảo điện thoại c&oacute; RAM 8 GB trở l&ecirc;n kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-ram-8gb-tro-len\" target=\"_blank\" rel=\"noopener\">RAM 12 GB</a>&nbsp;cho tốc độ nhanh, mượt m&agrave;, khả năng đa nhiệm tốt v&agrave;&nbsp;<a title=\"Tham khảo điện thoại c&oacute; bộ nhớ trong 256 GB kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-rom-128-den-256gb\" target=\"_blank\" rel=\"noopener\">bộ nhớ trong 256 GB</a>&nbsp;cho ph&eacute;p bạn thỏa th&iacute;ch lưu trữ ảnh cũng như video, những bộ phim y&ecirc;u th&iacute;ch m&agrave; kh&ocirc;ng lo giới hạn bởi dung lượng lưu trữ.</p>\r\n<p>L&agrave; chiếc smartphone cao cấp, c&ocirc;ng nghệ 5G đương nhi&ecirc;n kh&ocirc;ng thể thiếu tr&ecirc;n thiết bị. Samsung Galaxy Z Fold2 5G cho khả năng kết nối mạnh mẽ v&agrave; tốc độ xử l&yacute; nhanh ch&oacute;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-293920-083939.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Hỗ trợ kết nối mạng 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-293920-083939.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Hỗ trợ kết nối mạng 5G\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-293920-083939.jpg\" /></a></p>\r\n<h3>Dung lượng pin lớn 4500 mAh</h3>\r\n<p>Samsung Galaxy Z Fold2 5G l&agrave; chiếc smartphone c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh lớn n&ecirc;n cũng sẽ đ&ograve;i hỏi nhiều năng lượng hơn. Vi&ecirc;n pin k&eacute;p 4500 mAh đem đến nguồn năng lượng bất tận d&agrave;nh cho cả ng&agrave;y d&agrave;i.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282220-112219.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Pin lớn 4500 mAh, sử dụng được l&acirc;u\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282220-112219.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Pin lớn 4500 mAh, sử dụng được l&acirc;u\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282220-112219.jpg\" /></a></p>\r\n<p>C&ocirc;ng nghệ&nbsp;<a title=\"Tham khảo điện thoại sạc nhanh tại Thegioididong.com \" href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" rel=\"noopener\">sạc pin nhanh</a>&nbsp;c&oacute; d&acirc;y 25 W v&agrave; sạc nhanh kh&ocirc;ng d&acirc;y 11 W cho ph&eacute;p điện thoại được sạc nhanh ch&oacute;ng gi&uacute;p bạn trải nghiệm giải tr&iacute; liền mạch v&agrave; kh&ocirc;ng bỏ lỡ bất kỳ khoảnh khắc quan trọng n&agrave;o.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-294620-084623.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Hỗ trợ nhanh 25 W\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-294620-084623.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Hỗ trợ nhanh 25 W\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-294620-084623.jpg\" /></a></p>\r\n<h3>Vươn tầm cao mới với hệ thống camera tuyệt đỉnh</h3>\r\n<p>Hai camera selfie 10 MP ở cả m&agrave;n h&igrave;nh ch&iacute;nh v&agrave; m&agrave;n h&igrave;nh phụ đều được trang bị t&iacute;nh năng l&agrave;m đẹp AI v&agrave; chụp ch&acirc;n dung x&oacute;a ph&ocirc;ng sẽ gi&uacute;p bạn trở n&ecirc;n cuốn h&uacute;t hơn trong từng khung ảnh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-283320-113318.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Chụp h&igrave;nh selfie cực đỉnh với c&agrave;i đặt th&ocirc;ng minh AI\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-283320-113318.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Chụp h&igrave;nh selfie cực đỉnh với c&agrave;i đặt th&ocirc;ng minh AI\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-283320-113318.jpg\" /></a></p>\r\n<p>Trong khi camera ch&iacute;nh 12 MP hỗ trợ chống rung quang học c&ugrave;ng c&ocirc;ng nghệ lấy n&eacute;t Dual Pixel AF,&nbsp;<a title=\"Tham khảo điện thoại c&oacute; camera g&oacute;c si&ecirc;u rộng kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\" rel=\"noopener\">camera g&oacute;c si&ecirc;u rộng</a>&nbsp;12 MP với tầm nh&igrave;n l&ecirc;n đến 123 độ cho ph&eacute;p bắt trọn to&agrave;n bộ khung cảnh nhưng vẫn đảm bảo lấy n&eacute;t được c&aacute;c vật ở xa.</p>\r\n<p><a title=\"Tham khảo điện thoại c&oacute; camera tele kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-camera-zoom\" target=\"_blank\" rel=\"noopener\">Camera tele</a>&nbsp;12 MP c&oacute; khẩu độ f/2.4 hỗ trợ zoom quang học 2x, cho khả năng thu ph&oacute;ng mượt m&agrave;, gi&uacute;p người d&ugrave;ng zoom gần hơn tới đối tượng chụp m&agrave; kh&ocirc;ng l&agrave;m giảm chất lượng h&igrave;nh ảnh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254520-094504.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Camera tạo n&ecirc;n tuyệt t&aacute;c\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254520-094504.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Camera tạo n&ecirc;n tuyệt t&aacute;c\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-254520-094504.jpg\" /></a></p>\r\n<p>Nhờ thuật to&aacute;n AI th&ocirc;ng minh c&ugrave;ng với chế độ chụp ảnh ban đ&ecirc;m, hệ thống camera sẽ tự động lấy n&eacute;t để chụp li&ecirc;n tiếp nhiều tấm ảnh c&ugrave;ng một l&uacute;c, sau đ&oacute; gh&eacute;p ch&uacute;ng lại với nhau th&agrave;nh một bức ảnh ho&agrave;n mỹ nhất với độ r&otilde; n&eacute;t vượt trội, giảm nhiễu v&agrave; m&agrave;u sắc rực rỡ.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, m&aacute;y hỗ trợ quay video chất lượng 4K với tốc độ 60 fps, t&iacute;nh năng Auto Framing lấy n&eacute;t đỉnh cao v&agrave; theo s&aacute;t chủ thể để bạn lu&ocirc;n lu&ocirc;n l&agrave; t&acirc;m điểm của khung h&igrave;nh.</p>\r\n<h3>Flex Mode: T&iacute;nh năng ưu việt</h3>\r\n<p>Flex Mode cho bạn c&ugrave;ng l&uacute;c sử dụng cả hai phần m&agrave;n h&igrave;nh như khi chụp ảnh. Phần m&agrave;n h&igrave;nh ph&iacute;a tr&ecirc;n sẽ hiển thị ảnh từ camera, trong khi đ&oacute; phần m&agrave;n h&igrave;nh dưới l&agrave; giao diện điều khiển camera.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282120-112145.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Chế độ Flex Mode ưu việt\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282120-112145.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Chế độ Flex Mode ưu việt\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282120-112145.jpg\" /></a></p>\r\n<p>Hoặc bạn c&oacute; thể tận dụng tối đa k&iacute;ch thước rộng lớn như m&aacute;y t&iacute;nh bảng mini từ m&agrave;n h&igrave;nh ch&iacute;nh để sử dụng liền mạch đồng thời cả ba ứng dụng. Việc bạn cần l&agrave;m chỉ l&agrave; sử dụng ng&oacute;n tay k&eacute;o v&agrave; thả c&aacute;c ứng dụng cần thiết m&agrave; kh&ocirc;ng cần chuyển đổi qua lại giữa c&aacute;c menu.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282120-112151.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Fold2 5G - Trải nghiệm vượt trội\" src=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282120-112151.jpg\" alt=\"Điện thoại Samsung Galaxy Z Fold2 5G - Trải nghiệm vượt trội\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/232668/samsung-galaxy-z-fold2-5g-dac-biet-282120-112151.jpg\" /></a></p>\r\n<p>Samsung Galaxy Z Fold2 5G đ&atilde; cho thấy những nỗ lực của Samsung trong việc kh&ocirc;ng ngừng cải tiến, n&acirc;ng cấp để khắc phục những điểm yếu từ bản lề đến m&agrave;n h&igrave;nh ngo&agrave;i. Để giờ đ&acirc;y, chiếc smartphone n&agrave;y kh&ocirc;ng chỉ sở hữu ngoại h&igrave;nh sang trọng, hiệu năng, camera hay pin đều thuộc h&agrave;ng &ldquo;khủng&rdquo; tr&ecirc;n thị trường.</p>', 50000000, 'uploads/products/samsung-galaxy-z-fold-2-den-600x600-600x600.jpg', 34, '2', '2021-09-03 17:00:00', '2021-10-18 03:11:32', 1, NULL),
(8, 'iPhone 12 Pro Max 512GB', '<h3><a title=\"Tham khảo điện thoại iPhone 12 Pro Max 512GB ch&iacute;nh h&atilde;ng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/iphone-12-pro-max-512gb\" target=\"_blank\" rel=\"noopener\">iPhone 12 Pro Max 512GB</a>&nbsp;- đẳng cấp từ t&ecirc;n gọi đến từng chi tiết. Ngay từ khi chỉ l&agrave; tin đồn th&igrave; chiếc&nbsp;<a title=\"Tham khảo điện thoại ch&iacute;nh h&atilde;ng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">smartphone</a>&nbsp;n&agrave;y đ&atilde; l&agrave;m đứng ngồi kh&ocirc;ng y&ecirc;n bao &ldquo;fan cứng&rdquo; nh&agrave;&nbsp;<a title=\"Tham khảo mẫu sản phẩm Apple ch&iacute;nh h&atilde;ng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/apple\" target=\"_blank\" rel=\"noopener\">Apple</a>, với những n&acirc;ng cấp v&ocirc; c&ugrave;ng nổi bật hứa hẹn sẽ mang đến những trải nghiệm tốt nhất về mọi mặt m&agrave; chưa một chiếc&nbsp;<a title=\"Tham khảo điện thoại iPhone ch&iacute;nh h&atilde;ng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" rel=\"noopener\">iPhone</a>&nbsp;tiền nhiệm n&agrave;o c&oacute; được.</h3>', '<h3>Thu h&uacute;t từ c&aacute;i nh&igrave;n đầu ti&ecirc;n</h3>\r\n<p>Quay trở lại đầy ho&agrave;i niệm với thiết kế phẳng, vu&ocirc;ng vức tương tự&nbsp;<a title=\"Tham khảo th&ocirc;ng tin iPhone 4 tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd/iphone-4-8gb\" target=\"_blank\" rel=\"noopener\">iPhone 4</a>&nbsp;nhưng kh&ocirc;ng hề cho cảm gi&aacute;c lỗi thời m&agrave; ho&agrave;n to&agrave;n sang trọng với thiết kế tinh tế v&agrave; được cấu tạo từ những vật liệu cao cấp hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/228744/iphone-12-pro-max-512gb-144921-094924.jpg\"><img class=\"lazy\" title=\"Trở lại kiểu thiết kế vu&ocirc;ng vức từng c&oacute; tr&ecirc;n iPhone 4 | iPhone 12 Pro Max\" src=\"data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABAQMAAAAl21bKAAAAA1BMVEX6+vqsEtnpAAAACklEQVQI12NgAAAAAgAB4iG8MwAAAABJRU5ErkJggg==\" alt=\"Trở lại kiểu thiết kế vu&ocirc;ng vức từng c&oacute; tr&ecirc;n iPhone 4 | iPhone 12 Pro Max\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-144921-094924.jpg\" /></a></p>\r\n<p><a title=\"Tham khảo th&ocirc;ng tin iPhone 12 Pro Max kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd/iphone-12-pro-max\" target=\"_blank\" rel=\"noopener\">iPhone 12 Pro Max</a>&nbsp;được chế t&aacute;c từ mặt k&iacute;nh cường lực Ceramic Shield c&oacute; độ bền gấp 4 lần iPhone tiền nhiệm, phần khung cấu tạo từ th&eacute;p kh&ocirc;ng gỉ cực kỳ chắc chắn. Mang lại khả năng chống trầy, chống va đập tốt hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/228744/iphone-12-pro-max-512gb-122620-042638.jpg\"><img class=\"lazy\" title=\"Mặt k&iacute;nh Ceramic Shield | iPhone 12 Pro Max\" src=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-122620-042638.jpg\" alt=\"Mặt k&iacute;nh Ceramic Shield | iPhone 12 Pro Max\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-122620-042638.jpg\" /></a></p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"Mặt k&iacute;nh Ceramic Shield tr&ecirc;n iPhone l&agrave; g&igrave;? C&oacute; xịn s&ograve; như quảng c&aacute;o?\" href=\"https://www.thegioididong.com/hoi-dap/mat-kinh-ceramic-shield-tren-iphone-12-la-gi-co-xin-so-nhu-1298900\" target=\"_blank\" rel=\"noopener\">Mặt k&iacute;nh Ceramic Shield tr&ecirc;n iPhone l&agrave; g&igrave;? C&oacute; xịn s&ograve; như quảng c&aacute;o?</a></p>\r\n<h3>Super Retina XDR - mang đến trải nghiệm m&agrave;n h&igrave;nh ch&acirc;n thực</h3>\r\n<p>Sau những ph&agrave;n n&agrave;n về phần viền m&agrave;n h&igrave;nh của&nbsp;<a title=\"Tham khảo th&ocirc;ng tin iPhone 11 tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd/iphone-11\" target=\"_blank\" rel=\"noopener\">iPhone 11</a>&nbsp;series th&igrave; nay chiếc iPhone 12 Pro Max 512GB đ&atilde; được cải thiện triệt để với k&iacute;ch thước m&agrave;n h&igrave;nh 6.7 inch, tỉ lệ m&agrave;n h&igrave;nh so với th&acirc;n m&aacute;y tương đương 87.4% tăng tối đa diện t&iacute;ch hiển thị.</p>\r\n<p>C&ocirc;ng nghệ m&agrave;n h&igrave;nh Super Retina XDR mang đến trải nghiệm m&agrave;n h&igrave;nh ch&acirc;n thực từng chi tiết. Nhờ t&iacute;nh năng True Tone tự động điều chỉnh cho độ ch&iacute;nh x&aacute;c m&agrave;u sắc cao, &aacute;nh s&aacute;ng h&agrave;i h&ograve;a.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/228744/iphone-12-pro-max-512gb-145021-095032.jpg\"><img class=\"lazy\" title=\"C&ocirc;ng nghệ m&agrave;n h&igrave;nh Super Retina XDR OLED mang đến trải nghiệm ho&agrave;n hảo | iPhone 12 Pro Max\" src=\"data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABAQMAAAAl21bKAAAAA1BMVEX6+vqsEtnpAAAACklEQVQI12NgAAAAAgAB4iG8MwAAAABJRU5ErkJggg==\" alt=\"C&ocirc;ng nghệ m&agrave;n h&igrave;nh Super Retina XDR OLED mang đến trải nghiệm ho&agrave;n hảo | iPhone 12 Pro Max\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-145021-095032.jpg\" /></a></p>\r\n<p>Độ s&aacute;ng tối đa l&ecirc;n đến 1200 nits (HDR), hỗ trợ HDR 10, dải m&agrave;u P3 chuẩn điện ảnh đảm bảo đ&acirc;y l&agrave; c&ocirc;ng nghệ m&agrave;n h&igrave;nh tốt nhất từ trước đến nay của nh&agrave; Apple, đ&aacute;p ứng nhu cầu giải tr&iacute; ng&agrave;y c&agrave;ng thiết yếu của người d&ugrave;ng smartphone.</p>\r\n<h3>Hệ thống camera được n&acirc;ng cấp mạnh mẽ</h3>\r\n<p>iPhone 12 Pro Max được trang bị bộ 3 camera chuy&ecirc;n nghiệp với độ ph&acirc;n giải đều bằng 12 MP, trong đ&oacute; gồm một cảm biến ch&iacute;nh l&agrave;&nbsp;<a title=\"Tham khảo điện thoại c&oacute; camera g&oacute;c rộng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\" rel=\"noopener\">camera g&oacute;c rộng</a>, một camera g&oacute;c si&ecirc;u rộng 120 độ v&agrave; một camera chụp x&oacute;a ph&ocirc;ng. Ngo&agrave;i ra, 12 Pro Max c&ograve;n hỗ trợ một cảm biến LiDAR gi&uacute;p việc lấy n&eacute;t trong điều kiện thiếu s&aacute;ng nhanh hơn gấp 6 lần.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/228744/iphone-12-pro-max-512gb-145321-095330.jpg\"><img class=\"lazy\" title=\"Cụm 3 camera chất lượng | iPhone 12 Pro Max\" src=\"data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABAQMAAAAl21bKAAAAA1BMVEX6+vqsEtnpAAAACklEQVQI12NgAAAAAgAB4iG8MwAAAABJRU5ErkJggg==\" alt=\"Cụm 3 camera chất lượng | iPhone 12 Pro Max\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-145321-095330.jpg\" /></a></p>\r\n<p>iPhone 12 Pro Max c&oacute; cảm biến lớn hơn gi&uacute;p thu s&aacute;ng tốt hơn 27% v&agrave; c&oacute; c&ocirc;ng nghệ chống rung ti&ecirc;n tiến. Dựa v&agrave;o c&aacute;c yếu tố tr&ecirc;n, iPhone 12 Pro Max c&oacute; thể chụp những bức ảnh chất lượng, đầy m&agrave;u sắc, đủ chi tiết, d&ugrave; trong m&ocirc;i trường thiếu s&aacute;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/228744/iphone-12-pro-max-512gb-045920-025933.jpg\"><img class=\"lazy\" title=\"Cụm camera chụp ảnh sắc n&eacute;t, chi tiết | iPhone 12 Pro Max\" src=\"data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABAQMAAAAl21bKAAAAA1BMVEX6+vqsEtnpAAAACklEQVQI12NgAAAAAgAB4iG8MwAAAABJRU5ErkJggg==\" alt=\"Cụm camera chụp ảnh sắc n&eacute;t, chi tiết | iPhone 12 Pro Max\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-045920-025933.jpg\" /></a></p>\r\n<p>Tất cả mang đến những thước ảnh tự nhi&ecirc;n ho&agrave;n hảo như c&aacute;i nh&igrave;n trực tiếp từ đ&ocirc;i mắt của bạn.</p>\r\n<p>Camera trước cũng c&oacute; độ ph&acirc;n giải 12 MP hỗ trợ đầy đủ c&aacute;c t&iacute;nh năng như chế độ chụp ch&acirc;n dung với hiệu ứng bokeh, Memoji, Night Mode, Deep Fusion,... v&agrave; c&ograve;n nhiều t&iacute;nh năng kh&aacute;c.</p>\r\n<p>Cảm biến LiDAR đo đạc v&agrave; qu&eacute;t hiển thị sơ đồ 3D, l&agrave; một ưu điểm lớn cho người d&ugrave;ng l&agrave; &ldquo;d&acirc;n&rdquo; thiết kế.</p>', 41490000, 'uploads/products/iphone-12-pro-max-512gb-(2).jpg', 1, '2', '2021-09-25 17:00:00', '2021-10-18 02:49:19', 1, NULL),
(9, 'Laptop Asus VivoBook X509JA i3 1005G1/4GB/256GB/Win10', '<h2><a title=\"Laptop Asus VivoBook A515EA i3 1115G4/8GB/512GB/Win10 (BQ497T)\" href=\"https://www.thegioididong.com/laptop/asus-vivobook-a515ea-i3-bq497t\" target=\"_blank\" rel=\"noopener\" type=\"Laptop Asus VivoBook A515EA i3 1115G4/8GB/512GB/Win10 (BQ497T)\">Laptop Asus VivoBook i3 A515EA (BQ497T)</a>&nbsp;hiện đại với mức gi&aacute; tốt, được trang bị chip xử l&yacute; Intel thế hệ 11 mới nhất c&ugrave;ng bộ nhớ khủng, rất đ&aacute;ng để giới học sinh sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng sở hữu.</h2>', '<h3>Thiết kế mỏng nhẹ, năng động</h3>\r\n<p>Laptop Asus VivoBook i3 A515EA (BQ497T) c&oacute; kiểu d&aacute;ng trẻ trung, m&agrave;u sắc sang trọng đẹp mắt với gam m&agrave;u bạc thời trang.&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\">Laptop</a>&nbsp;mỏng&nbsp;<strong>17.9 mm</strong>&nbsp;v&agrave; c&oacute; khối lượng&nbsp;<strong>1.8 kg</strong>&nbsp;dễ d&agrave;ng để c&aacute;c bạn cất gọn v&agrave;o ba l&ocirc; mang theo đi học hằng ng&agrave;y.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043444.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043444.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) Thiết kế\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043444.jpg\" /></a></p>\r\n<p>Thiết kế b&agrave;n ph&iacute;m fullsize với h&agrave;nh tr&igrave;nh ph&iacute;m&nbsp;<strong>1.4 mm</strong>&nbsp;cảm gi&aacute;c nhập liệu l&acirc;u kh&ocirc;ng bị mỏi. Hơi đ&aacute;ng tiếc khi b&agrave;n ph&iacute;m của d&ograve;ng VivoBook n&agrave;y kh&ocirc;ng được trang bị đ&egrave;n nền, nếu kh&ocirc;ng th&igrave; độ ho&agrave;n hảo sẽ c&agrave;ng tăng cao.&nbsp;</p>\r\n<p>Cảm biến v&acirc;n tay nhạy b&eacute;n được t&iacute;ch hợp gọn g&agrave;ng tr&ecirc;n touchpad gi&uacute;p bạn đăng nhập nhanh ch&oacute;ng v&agrave; an to&agrave;n.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043449.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cảm biến v&acirc;n tay\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043449.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cảm biến v&acirc;n tay\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043449.jpg\" /></a></p>\r\n<p>Được trang bị c&aacute;c cổng bao gồm USB 3.2, USB 2.0, HDMI, USB Type-C với tốc độ truyền tải dữ liệu ổn định,&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm Asus VivoBook đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus-vivobook\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm Asus VivoBook đang b&aacute;n tại Thegioididong.com\">Asus VivoBook</a>&nbsp;A515EA dễ d&agrave;ng kết nối m&aacute;y chiếu, tivi hay c&aacute;c thiết bị ngoại vi hỗ trợ kh&aacute;c.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043454.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cổng kết nối\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043454.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cổng kết nối\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043454.jpg\" /></a></p>\r\n<h3>Lướt web, xem phim mượt m&agrave;</h3>\r\n<p>Asus VivoBook A515EA được trang bị chip Intel&nbsp;<strong><a title=\"Xem th&ecirc;m c&aacute;c laptop Core i3 đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=core-i3\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c laptop Core i3 đang b&aacute;n tại Thegioididong.com\">Core i3</a></strong>&nbsp;thuộc&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c laptop Intel Gen 11 đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-cpu-intel-gen-11\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c laptop Intel Gen 11 đang b&aacute;n tại Thegioididong.com\">thế hệ 11</a>&nbsp;mới nhất với bộ xử l&yacute; m&atilde; 1115G4 cho hiệu năng đủ d&ugrave;ng, thao t&aacute;c mượt m&agrave; với c&aacute;c ứng dụng như Office 365, tr&igrave;nh duyệt Google Chrome, Microsoft Edge, tốc độ tải dữ liệu ổn định.</p>\r\n<p><a title=\"Laptop được trang bị RAM 8 GB đang kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=8-gb\" target=\"_blank\" rel=\"noopener\" type=\"Laptop được trang bị RAM 8 GB đang kinh doanh tại Thegioididong.com\"><strong>RAM 8 GB</strong></a>&nbsp;cho khả năng đa nhiệm tốt, bạn c&oacute; thể vừa chạy deadline, vừa nghe một bản nhạc kh&ocirc;ng lời để giải toả căng thẳng m&agrave; kh&ocirc;ng l&agrave;m ảnh hưởng đến mạch l&agrave;m việc.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043513.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cấu h&igrave;nh\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043513.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cấu h&igrave;nh\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043513.jpg\" /></a></p>\r\n<p>Hơn nữa, m&aacute;y c&ograve;n c&oacute;&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus c&oacute; SSD tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus-o-cung-ssd\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus c&oacute; SSD tại Thegioididong.com\">ổ cứng SSD</a>&nbsp;512 GB cho tốc độ khởi động laptop hay mở c&aacute;c ứng dụng đều chỉ trong v&ograve;ng v&agrave;i gi&acirc;y. V&agrave; với dung lượng&nbsp;<strong><a title=\"Xem th&ecirc;m c&aacute;c laptop c&oacute; SSD 512 GB\" href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c laptop c&oacute; SSD 512 GB\">512 GB</a></strong>&nbsp;th&igrave; việc lưu trữ h&igrave;nh ảnh, t&agrave;i liệu học tập,&hellip; cũng thoải m&aacute;i hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043503.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - SSD\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043503.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - SSD\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043503.jpg\" /></a></p>\r\n<p>Chưa dừng lại ở đ&oacute;, VivoBook A515EA c&ograve;n c&oacute; t&iacute;nh năng Link to My ASUS, thiết bị di động của bạn sẽ được kết nối v&agrave; hiển thị tr&ecirc;n m&aacute;y t&iacute;nh Asus, bạn dễ d&agrave;ng thao t&aacute;c c&aacute;c chức năng của thiết bị di động th&ocirc;ng qua chuột m&aacute;y t&iacute;nh, b&agrave;n ph&iacute;m của VivoBook A515EA.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043518.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Phần mềm myAsus\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043518.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Phần mềm myAsus\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043518.jpg\" /></a></p>\r\n<h3>M&agrave;n h&igrave;nh tr&agrave;n viền nh&igrave;n cực đ&atilde; mắt</h3>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus đang b&aacute;n tại Thegioididong.com\">Laptop Asus</a>&nbsp;VivoBook A515EA sở hữu m&agrave;n h&igrave;nh&nbsp;<a title=\"Xem th&ecirc;m một số laptop m&agrave;n h&igrave;nh tr&ecirc;n 15 inch đang được kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-tren-15-inch\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m một số laptop m&agrave;n h&igrave;nh tr&ecirc;n 15 inch đang được kinh doanh tại Thegioididong.com\"><strong>15.6 inch</strong></a>&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus c&oacute; Viền m&agrave;n h&igrave;nh mỏng tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus-vien-man-hinh-mong\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus c&oacute; Viền m&agrave;n h&igrave;nh mỏng tại Thegioididong.com\">viền mỏng</a>&nbsp;c&oacute; độ ph&acirc;n giải Full HD cho ra những thước phim m&agrave;u sắc sống động h&uacute;t mắt. Tấm nền IPS cho g&oacute;c nh&igrave;n rộng gi&uacute;p tạo cảm gi&aacute;c kh&ocirc;ng gian hiển thị rộng r&atilde;i v&agrave; thoải m&aacute;i hơn kết hợp c&ugrave;ng c&ocirc;ng nghệ chống ch&oacute;i Anti Glare, l&agrave;m việc nơi g&oacute;c cửa sổ &aacute;nh s&aacute;ng chiếu ch&iacute;nh diện v&agrave;o m&agrave;n h&igrave;nh cũng kh&ocirc;ng l&agrave;m ảnh hưởng năng suất l&agrave;m việc của bạn.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043508.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - M&agrave;n h&igrave;nh\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043508.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - M&agrave;n h&igrave;nh\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043508.jpg\" /></a></p>\r\n<p>Hiện đại v&agrave; tiện dụng l&agrave; những cụm từ d&ugrave;ng để m&ocirc; tả chiếc&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop học tập, văn ph&ograve;ng đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop học tập, văn ph&ograve;ng đang b&aacute;n tại Thegioididong.com\">laptop học tập văn ph&ograve;ng</a>&nbsp;Asus VivoBook A515EA (BQ497T). Với thiết kế mỏng nhẹ c&ugrave;ng dung lượng khủng của ổ cứng SSD, chiếc laptop n&agrave;y sẽ đem đến những trải nghiệm tuyệt vời cho người d&ugrave;ng, đặc biệt l&agrave; với giới trẻ.&nbsp;</p>', 14090000, 'uploads/products/asus-vivobook-x509ja-i3-ej480t-225608-200x200.jpg', 3, '2', '2021-09-10 17:00:00', '2021-10-18 12:41:17', 2, '2021-10-18 12:41:17');
INSERT INTO `products` (`id`, `name`, `desc`, `content`, `price`, `thumbnail`, `qty`, `status`, `created_at`, `updated_at`, `cat_id`, `deleted_at`) VALUES
(11, 'Laptop Lenovo IdeaPad S145 15IIL i3 1005G1/4GB/256GB/Win10', '<h2><a title=\"Laptop Lenovo IdeaPad S145 15IIL i3 (81W8001XVN) đang được kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop/lenovo-ideapad-s145-15iil-i3-1005g1-4gb-256gb-win1#11\" target=\"_blank\" rel=\"noopener\" type=\"Laptop Lenovo IdeaPad S145 15IIL i3 (81W8001XVN) đang được kinh doanh tại Thegioididong.com\">Laptop Lenovo IdeaPad S145 15IIL i3 (81W8001XVN)</a>&nbsp;thuộc ph&acirc;n kh&uacute;c&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop học tập, văn ph&ograve;ng đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop học tập, văn ph&ograve;ng đang b&aacute;n tại Thegioididong.com\">laptop học tập văn ph&ograve;ng</a>&nbsp;cơ bản với mức gi&aacute; tốt. M&aacute;y c&oacute; cấu h&igrave;nh ổn, đủ chạy c&aacute;c ứng dụng văn ph&ograve;ng phổ biến, điểm nổi bật của Lenovo IdeaPad S145 l&agrave; ổ cứng SSD si&ecirc;u nhanh, thiết kế mỏng gọn, tinh tế.</h2>', '<h3>Thiết kế đơn giản, thời trang v&agrave; tinh tế</h3>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\">Laptop</a>&nbsp; mang thiết kế cơ bản của d&ograve;ng&nbsp;<a title=\"Laptop Lenovo Ideapad S145 15IWL (81MV00SXVN)\" href=\"https://www.thegioididong.com/laptop/lenovo-ideapad-s145\" target=\"_blank\" rel=\"noopener\">Lenovo IdeaPad S145</a>&nbsp;c&oacute; ngoại h&igrave;nh đẹp mắt, lớp vỏ được l&agrave;m bằng nhựa phủ sơn m&agrave;u x&aacute;m sang trọng với logo Lenovo được đặt gọn g&agrave;ng sang một b&ecirc;n tr&ecirc;n nắp lưng. Độ d&agrave;y&nbsp;<strong>17.9 mm</strong>, c&acirc;n nặng&nbsp;<strong>1.79 kg</strong>&nbsp;ph&ugrave; hợp với c&aacute;c bạn học sinh sinh vi&ecirc;n, người thường xuy&ecirc;n di chuyển.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/216292/lenovo-ideapad-s145-81w8001xvn-179kg.jpg\"><img class=\"lazy\" title=\"Laptop c&oacute; ngoại h&igrave;nh đẹp mắt, lớp vỏ được l&agrave;m bằng nhựa \" src=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-179kg.jpg\" alt=\"Laptop c&oacute; ngoại h&igrave;nh đẹp mắt, lớp vỏ được l&agrave;m bằng nhựa \" data-original=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-179kg.jpg\" /></a></p>\r\n<h3>Đ&aacute;p ứng tốt nhu cầu học tập, văn ph&ograve;ng</h3>\r\n<p>M&aacute;y được trang bị con chip&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c laptop Core i3 đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=core-i3\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c laptop Core i3 đang b&aacute;n tại Thegioididong.com\"><strong>Intel Core i3</strong></a>&nbsp;Ice Lake thế hệ 10 v&agrave;&nbsp;<strong><a title=\"Laptop được trang bị RAM 4 GB đang kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" rel=\"noopener\" type=\"Laptop được trang bị RAM 4 GB đang kinh doanh tại Thegioididong.com\">RAM 4 GB</a></strong>. Với cấu h&igrave;nh n&agrave;y, d&acirc;n văn ph&ograve;ng hay c&aacute;c bạn học sinh, sinh vi&ecirc;n c&oacute; thể y&ecirc;n t&acirc;m xử l&iacute; c&aacute;c t&aacute;c vụ thường ng&agrave;y như soạn thảo Word, soạn b&agrave;i thuyết tr&igrave;nh tr&ecirc;n PowerPoint, lướt web, nghe nhạc, ... mượt m&agrave;.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/216292/lenovo-ideapad-s145-81w8001xvn-i3-10.jpg\"><img class=\"lazy\" title=\"M&aacute;y được trang bị con chip Intel Core i3 Ice Lake thế hệ 10 v&agrave; RAM 4 GB, card t&iacute;ch hợp \" src=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-i3-10.jpg\" alt=\"M&aacute;y được trang bị con chip Intel Core i3 Ice Lake thế hệ 10 v&agrave; RAM 4 GB, card t&iacute;ch hợp \" data-original=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-i3-10.jpg\" /></a></p>\r\n<h3>Khởi động nhanh, kh&ocirc;ng qu&aacute; 20 gi&acirc;y</h3>\r\n<p><strong><a title=\"Laptop Lenovo ch&iacute;nh h&atilde;ng được trang bị ổ cứng SSD đang được kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-lenovo-o-cung-ssd\" target=\"_blank\" rel=\"noopener\" type=\"Laptop ch&iacute;nh h&atilde;ng được trang bị ổ cứng SSD đang được kinh doanh tại Thegioididong.com\">Ổ cứng SSD</a>&nbsp;256 GB</strong>&nbsp;cho tốc độ đọc v&agrave; ghi dữ liệu cực k&igrave; nhanh, mở c&aacute;c ứng dụng thường d&ugrave;ng như Word, Excel chỉ mất 3-5 gi&acirc;y. Dung lượng 256 GB kh&ocirc;ng qu&aacute; dư giả, vừa đủ cho nhu cầu lưu trữ t&agrave;i liệu, h&igrave;nh ảnh, ứng dụng...</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/216292/lenovo-ideapad-s145-81w8001xvn-256gb.jpg\"><img class=\"lazy\" title=\"Khởi động nhanh, kh&ocirc;ng qu&aacute; 20 gi&acirc;y\" src=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-256gb.jpg\" alt=\"Khởi động nhanh, kh&ocirc;ng qu&aacute; 20 gi&acirc;y\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-256gb.jpg\" /></a></p>\r\n<h3>M&agrave;n h&igrave;nh viền mỏng thời thượng</h3>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Lenovo đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-lenovo\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Lenovo đang b&aacute;n tại Thegioididong.com\">Laptop Lenovo</a>&nbsp;trang bị m&agrave;n h&igrave;nh&nbsp;<strong><a title=\"Xem th&ecirc;m một số laptop m&agrave;n h&igrave;nh tr&ecirc;n 15 inch đang được kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-tren-15-inch\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m một số laptop m&agrave;n h&igrave;nh tr&ecirc;n 15 inch đang được kinh doanh tại Thegioididong.com\">15.6 inch</a></strong>&nbsp;<strong>Full HD</strong>&nbsp;gi&uacute;p cho h&igrave;nh ảnh sắc n&eacute;t, r&otilde; r&agrave;ng, m&agrave;u sắc tương phản tốt.&nbsp;<a title=\"C&aacute;c mẫu laptop Lenovo c&oacute; viền mỏng tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-lenovo-vien-man-hinh-mong\" target=\"_blank\" rel=\"noopener\">Viền m&agrave;n h&igrave;nh mỏng</a>&nbsp;theo kịp xu hướng thiết kế 2020 cho cảm gi&aacute;c sexy hơn, k&iacute;ch thước m&aacute;y cũng gọn g&agrave;ng hơn so với c&aacute;c laptop 15.6 inch kh&aacute;c.</p>\r\n<p>C&ocirc;ng nghệ chống ch&oacute;i Anti-Glare giảm t&igrave;nh trạng l&oacute;a, nh&igrave;n r&otilde; hơn ngay cả khi sử dụng nơi cửa sổ c&oacute; &aacute;nh s&aacute;ng mạnh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/216292/lenovo-ideapad-s145-81w8001xvn-156inch.jpg\"><img class=\"lazy\" title=\"Laptop Lenovo trang bị m&agrave;n h&igrave;nh 15.6 inch Full HD\" src=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-156inch.jpg\" alt=\"Laptop Lenovo trang bị m&agrave;n h&igrave;nh 15.6 inch Full HD\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-156inch.jpg\" /></a></p>\r\n<h3>Dễ d&agrave;ng kết nối với m&aacute;y chiếu, truyền dữ liệu ...</h3>\r\n<p>M&aacute;y trang bị 2 cổng USB 3.0, 1 cổng HDMI v&agrave; USB 2.0. Đ&acirc;y l&agrave; c&aacute;c loại cổng kết nối th&ocirc;ng dụng gi&uacute;p bạn nhập v&agrave; xuất dữ liệu sang thiết bị kh&aacute;c m&agrave; kh&ocirc;ng cần đầu chuyển đổi.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/216292/lenovo-ideapad-s145-81w8001xvn-usb.jpg\"><img class=\"lazy\" title=\"Dễ d&agrave;ng kết nối với m&aacute;y chiếu, truyền dữ liệu...\" src=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-usb.jpg\" alt=\"Dễ d&agrave;ng kết nối với m&aacute;y chiếu, truyền dữ liệu...\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-usb.jpg\" /></a></p>\r\n<h3>Pin sử dụng được cả ng&agrave;y</h3>\r\n<p>Pin của&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm Lenovo IdeaPad đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-lenovo-ideapad\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm Lenovo IdeaPad đang b&aacute;n tại Thegioididong.com\">Lenovo IdeaPad</a>&nbsp;sử dụng được khoảng&nbsp;<strong>6 - 7 giờ</strong>&nbsp;li&ecirc;n tục đối với c&aacute;c t&aacute;c vụ học tập, văn ph&ograve;ng, đủ để bạn sử dụng suốt một ng&agrave;y m&agrave; kh&ocirc;ng cần cắm sạc.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/216292/lenovo-ideapad-s145-81w8001xvn-7tie%CC%82%CC%81ng.jpg\"><img class=\"lazy\" title=\"Pin của Lenovo IdeaPad sử dụng được khoảng 6 - 7 giờ\" src=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-7tie%CC%82%CC%81ng.jpg\" alt=\"Pin của Lenovo IdeaPad sử dụng được khoảng 6 - 7 giờ\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/216292/lenovo-ideapad-s145-81w8001xvn-7tiếng.jpg\" /></a></p>', 8840000, 'uploads/products/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-(2).jpg', 23, '2', '2021-09-01 17:00:00', '2021-10-18 03:11:51', 2, NULL),
(12, 'Laptop HP Pavilion x360 dw1016TU i3 1115G4/4GB/256GB/Touch/Pen/Office H&S2019/Win10', '<h2>Laptop 2 trong 1&nbsp;<a title=\"Laptop HP Pavilion x360 dw1016TU i3 (2H3Q0PA)\" href=\"https://www.thegioididong.com/laptop/hp-pavilion-x360-dw1016tu-i3-2h3q0pa\" target=\"_blank\" rel=\"noopener\">HP Pavilion x360 dw1016TU i3 (2H3Q0PA)</a>&nbsp;với cấu h&igrave;nh đủ d&ugrave;ng, trọng lượng mỏng nhẹ thỏa sức dịch chuyển c&ugrave;ng cơ chế sử dụng linh hoạt cho bạn thỏa sức l&agrave;m việc, giải tr&iacute; v&agrave; s&aacute;ng tạo mỗi ng&agrave;y.&nbsp;</h2>', '<h3>Thiết kế mỏng nhẹ, bản lề gập xoay 360 độ</h3>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c sản phẩm HP Probook  đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-hp-compaq-pavilion\" target=\"_blank\" rel=\"noopener\">HP Pavilion</a>&nbsp;x360 to&aacute;t l&ecirc;n sự cao cấp nhờ thiết kế mỏng nhẹ, đường n&eacute;t mạnh mẽ nhưng kh&ocirc;ng k&eacute;m phần tinh tế. Độ mỏng ấn tượng chỉ&nbsp;<strong>19 mm</strong>&nbsp;v&agrave;&nbsp;<strong>1.65 kg</strong>&nbsp;thỏa sức dịch chuyển, cực k&igrave; ph&ugrave; hợp với nhu cầu di chuyển thường xuy&ecirc;n của giới trẻ hiện đại.</p>\r\n<p>Bản lề của HP Pavilion c&oacute; t&iacute;nh năng&nbsp;<strong>gập xoay 360</strong>&nbsp;độ cộng th&ecirc;m m&agrave;n h&igrave;nh cảm ứng đa điểm gi&uacute;p bạn c&oacute; thể t&ugrave;y chỉnh giữa nhiều chế độ sử dụng như chế độ laptop như b&igrave;nh thường, chế độ lều khi trao đổi th&ocirc;ng tin, họp nh&oacute;m hay gập lại để sử dụng như một chiếc m&aacute;y t&iacute;nh bảng thực thụ cực k&igrave; tiện lợi.&nbsp;</p>\r\n<p>M&aacute;y c&oacute; c&aacute;c cổng kết nối th&ocirc;ng dụng cho c&ocirc;ng việc văn ph&ograve;ng như 2 cổng USB 3.1, HDMI v&agrave; USB Type-C truyền dữ liệu tốc độ cao cộng với khe thẻ nhớ Micro SD gi&uacute;p bạn chia sẻ dữ liệu dễ d&agrave;ng giữa laptop v&agrave; c&aacute;c thiết bị kh&aacute;c.</p>\r\n<p>Với phương thức kết nối kh&ocirc;ng d&acirc;y<strong>&nbsp;Bluetooth v5.0</strong>&nbsp;v&agrave;&nbsp;<strong>Wi-Fi 802.11 a/b/g/n/ac</strong>&nbsp;cho ph&eacute;p bạn kết nối với đường truyền tốc độ cao v&agrave; ổn định để l&agrave;m việc, học trực tuyến, xem phim, lướt web,...</p>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop HP đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-hp-compaq\" target=\"_blank\" rel=\"noopener\">Laptop HP</a>&nbsp;c&oacute;&nbsp;<strong>cảm biến v&acirc;n tay&nbsp;</strong>để mở kh&oacute;a thiết bị, thanh to&aacute;n trực tuyến một c&aacute;ch rất nhanh ch&oacute;ng, tiện lợi so với nhập mật khẩu truyền thống m&agrave; c&ograve;n bảo mật an to&agrave;n hơn.</p>\r\n<h3>Cấu h&igrave;nh ổn định, xử l&iacute; nhanh&nbsp;</h3>\r\n<p>Laptop HP&nbsp;Pavilion x360 sở hữu sức mạnh của vi xử l&iacute;&nbsp;<strong>Intel&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c laptop Core i3 đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=core-i3\" target=\"_blank\" rel=\"noopener\">Core i3</a>&nbsp;Tiger Lake&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c laptop Intel Gen 11 đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-cpu-intel-gen-11\" target=\"_blank\" rel=\"noopener\">gen 11</a>&nbsp;</strong>sản xuất tr&ecirc;n tiến tr&igrave;nh 10nm, xung nhịp trung b&igrave;nh 3.00 GHz, tốc độ tối đa l&agrave; 4.1 GHz cho khả năng xử l&iacute; mạnh mẽ, sử dụng l&otilde;i Willow Cove ti&ecirc;u tốn &iacute;t năng lượng hơn. T&iacute;ch hợp trong con chip n&agrave;y l&agrave; card đồ họa&nbsp;Intel UHD Graphics tăng khả năng đồ họa để h&igrave;nh ảnh hiển thị sắc n&eacute;t hơn.</p>\r\n<p>B&ecirc;n cạnh đ&oacute; m&aacute;y trang bị&nbsp;<a title=\"Laptop được trang bị RAM 4 GB đang kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" rel=\"noopener\"><strong>4 GB RAM</strong></a>&nbsp;DDR4 với tốc độ Bus 3200 MHz cho khả năng đa nhiệm ổn, c&oacute; thể sử dụng nhiều ứng dụng c&ugrave;ng l&uacute;c, chẳng hạn như vừa l&agrave;m việc tr&ecirc;n Word vừa tham khảo t&agrave;i liệu từ h&agrave;ng chục tab Chrome. M&aacute;y c&ograve;n hỗ trợ khe RAM tối đa l&ecirc;n đến 32 GB thuận tiện cho việc n&acirc;ng cấp về sau.</p>\r\n<p><strong><a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop c&oacute; SSD tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-o-cung-ssd\" target=\"_blank\" rel=\"noopener\">Ổ cứng SSD</a>&nbsp;256 GB NVMe PCIe</strong>&nbsp;cho tốc độ khởi chạy Windows, c&aacute;c t&aacute;c vụ đều diễn ra mượt m&agrave;, nhanh ch&oacute;ng. Dung lượng&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c laptop c&oacute; SSD 256 GB\" href=\"https://www.thegioididong.com/laptop?g=ssd-256-gb\" target=\"_blank\" rel=\"noopener\">256 GB</a>&nbsp;đủ d&ugrave;ng để lưu trữ thoải m&aacute;i c&aacute;c t&agrave;i liệu l&agrave;m việc cũng như phim ảnh,...</p>\r\n<h3>M&agrave;n h&igrave;nh cảm ứng đa điểm, hiển thị sắc n&eacute;t</h3>\r\n<p>HP Pavilion x360 trang bị m&agrave;n h&igrave;nh&nbsp;<a title=\"Xem th&ecirc;m một số laptop m&agrave;n h&igrave;nh 14 inch đang được kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-14-inch\" target=\"_blank\" rel=\"noopener\"><strong>14 inch</strong></a>&nbsp;<strong>Full HD</strong>, được trang bị th&ecirc;m tấm nền IPS cho khung h&igrave;nh ảnh sắc n&eacute;t, m&agrave;u sắc rực rỡ, chất lượng hiển thị đồng nhất ở nhiều g&oacute;c nh&igrave;n. Viền m&agrave;n h&igrave;nh được thiết kế mỏng ở 3 cạnh đem đến cảm gi&aacute;c nh&igrave;n dễ chịu, kh&ocirc;ng bị sao nh&atilde;ng khi l&agrave;m việc.&nbsp;</p>\r\n<p>M&agrave;n h&igrave;nh c&oacute; t&iacute;nh năng&nbsp;<strong>cảm ứng&nbsp;</strong>cực nhạy, c&aacute;c thao t&aacute;c chạm, vuốt, thu ph&oacute;ng lu&ocirc;n mượt m&agrave; để bạn c&oacute; thể sử dụng thật thoải m&aacute;i ở mọi chế độ như lướt web, chỉnh sửa ảnh trực tiếp bằng tay như tr&ecirc;n m&aacute;y t&iacute;nh bảng.&nbsp;</p>\r\n<p>Tuy c&oacute; viền rất mỏng nhưng m&aacute;y t&iacute;nh vẫn trang bị một HD webcam để người d&ugrave;ng c&oacute; thể tr&ograve; chuyện trực tuyến, họp online với h&igrave;nh ảnh r&otilde; n&eacute;t.</p>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\" href=\"https://https//www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\">Laptop</a>&nbsp;sử dụng c&ocirc;ng nghệ &acirc;m thanh&nbsp;<strong>Bang &amp; Olufsen audi</strong>o t&iacute;ch hợp c&aacute;c khả năng lọc tiếng ồn, gi&uacute;p &acirc;m thanh to v&agrave; r&otilde; r&agrave;ng hơn để bạn thoải m&aacute;i nghe nhạc, học online hay giải tr&iacute; với c&aacute;c tựa phim.</p>\r\n<p>HP Pavilion x360 dw1016TU i3 (2H3Q0PA) l&agrave; chiếc&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c Laptop 2 trong 1 đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-man-hinh-gap-360-do\" target=\"_blank\" rel=\"noopener\">laptop 2 trong 1</a>&nbsp;mỏng gọn v&agrave; hiện đại, ph&ugrave; hợp với học sinh sinh vi&ecirc;n, người s&aacute;ng tạo nội dung, d&acirc;n văn ph&ograve;ng,...</p>', 14490000, 'uploads/products/360-600x600.jpg', 43, '2', '2021-09-21 17:00:00', '2021-10-18 03:12:11', 2, NULL),
(13, 'Laptop Asus VivoBook A515EP i5 1135G7/8GB/512GB/2GB MX330/Win10', '<h2>ới m&agrave;u bạc sang trọng v&agrave; ph&iacute;m Enter viền v&agrave;ng neon, bộ vi xử l&yacute; Intel Core i5 Gen 11,&nbsp;<a title=\"Laptop Asus VivoBook A515EP i5 1135G7/8GB/512GB/2GB MX330/Win10 (BQ194T)\" href=\"https://www.thegioididong.com/laptop/asus-vivobook-a515ep-i5-bq194t\" target=\"_blank\" rel=\"noopener\" type=\"Laptop Asus VivoBook A515EP i5 1135G7/8GB/512GB/2GB MX330/Win10 (BQ194T)\">laptop Asus VivoBook A515EP</a>&nbsp;mang tới sức mạnh c&acirc;n tất mọi t&aacute;c vụ l&agrave;m việc&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop học tập, văn ph&ograve;ng đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop học tập, văn ph&ograve;ng đang b&aacute;n tại Thegioididong.com\">văn ph&ograve;ng, học tập</a>&nbsp;thường ng&agrave;y.&nbsp;</h2>', '<h3>Phong c&aacute;ch thời thượng</h3>\r\n<p>VivoBook 15 với nắp kim loại sang trọng m&agrave;u bạc soda đậm chất thời trang ph&ugrave; hợp với mọi đối tượng. Trọng lượng tổng thể chỉ&nbsp;<strong>1.8 kg</strong>&nbsp;v&agrave; thiết kế si&ecirc;u mỏng, laptop Asus VivoBook A515EP dễ d&agrave;ng nằm gọn trong balo để bạn c&oacute; thể mang theo bất k&igrave; nơi đ&acirc;u. Đặc biệt, bản lề của m&aacute;y được thiết kế kh&aacute; th&ocirc;ng minh gi&uacute;p n&acirc;ng m&aacute;y l&ecirc;n một g&oacute;c khoảng&nbsp;<strong>2 độ</strong>&nbsp;hỗ trợ cho việc tản nhiệt cũng như g&otilde; ph&iacute;m được tốt hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/233864/asus-vivobook-a515ep-i5-bq194t-185021-085053.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EP - Thiết kế \" src=\"https://cdn.tgdd.vn/Products/Images/44/233864/asus-vivobook-a515ep-i5-bq194t-185021-085053.jpg\" alt=\"Laptop Asus VivoBook A515EP - Thiết kế \" data-original=\"https://cdn.tgdd.vn/Products/Images/44/233864/asus-vivobook-a515ep-i5-bq194t-185021-085053.jpg\" /></a></p>\r\n<p>B&agrave;n ph&iacute;m k&iacute;ch cỡ đầy đủ sẽ l&agrave; sự trang bị ho&agrave;n hảo cho c&aacute;c t&aacute;c vụ nhập liệu. H&agrave;nh tr&igrave;nh ph&iacute;m 1.4 mm mang đến cảm gi&aacute;c g&otilde; ph&iacute;m vừa tay, thoải m&aacute;i. Với ph&iacute;m Enter viền v&agrave;ng neon độc đ&aacute;o xuất hiện như một điểm nhấn cho chiếc&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\">laptop</a>&nbsp;thời trang n&agrave;y.</p>\r\n<p>Mở kh&oacute;a&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus đang b&aacute;n tại Thegioididong.com\">laptop Asus</a>&nbsp;VivoBook A515EP dễ d&agrave;ng v&agrave; bảo mật hơn bao giờ hết nhờ cảm biến v&acirc;n tay t&iacute;ch hợp nơi g&oacute;c phải tr&ecirc;n của chuột cảm ứng, chỉ với một lần chạm bạn sẽ kh&ocirc;ng cần nhập mật khẩu mỗi khi đăng nhập.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/233864/asus-vivobook-a515ep-i5-bq194t-185121-085141.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EP - V&acirc;n tay\" src=\"https://cdn.tgdd.vn/Products/Images/44/233864/asus-vivobook-a515ep-i5-bq194t-185121-085141.jpg\" alt=\"Laptop Asus VivoBook A515EP - V&acirc;n tay\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/233864/asus-vivobook-a515ep-i5-bq194t-185121-085141.jpg\" /></a></p>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c sản phẩm Asus VivoBook đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus-vivobook\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm Asus VivoBook đang b&aacute;n tại Thegioididong.com\">Asus VivoBook</a>&nbsp;A515EP được trang bị cổng USB 3.1, Type-C, USB 2.0, cổng ra HDMI v&agrave; đầu đọc thẻ microSD hỗ trợ kết nối c&aacute;c thiết bị ngoại vi hiện đại, m&agrave;n h&igrave;nh v&agrave; m&aacute;y chiếu.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/233864/asus-vivobook-a515ep-i5-bq194t-185021-085040.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EP - Kết nối\" src=\"https://cdn.tgdd.vn/Products/Images/44/233864/asus-vivobook-a515ep-i5-bq194t-185021-085040.jpg\" alt=\"Laptop Asus VivoBook A515EP - Kết nối\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/233864/asus-vivobook-a515ep-i5-bq194t-185021-085040.jpg\" /></a></p>', 17990000, 'uploads/products/asus-vivobook-a515ea-i3-bq497t-(2) (1).jpg', 0, '2', '2021-09-19 17:00:00', NULL, 2, NULL),
(14, 'Laptop Lenovo IdeaPad S145 15IIL i5 1035G1/8GB/512GB/Win10', '<h2><a title=\"Lenovo IdeaPad S145 15IIL i5 (81W80021VN) đang được kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1\" target=\"_blank\" rel=\"noopener\" type=\"Lenovo IdeaPad S145 15IIL i5 (81W80021VN) đang được kinh doanh tại Thegioididong.com\">Lenovo IdeaPad S145 15IIL i5 (81W80021VN)</a>&nbsp;l&agrave; mẫu laptop c&oacute; cấu h&igrave;nh đ&aacute;p ứng tốt c&ocirc;ng việc văn ph&ograve;ng, học tập. Thiết bị sở hữu ổ cứng SSD cho tốc độ xử l&iacute; nhanh ch&oacute;ng, m&agrave;n h&igrave;nh Full HD sắc n&eacute;t.</h2>', '<h3>Thiết kế thanh lịch, gọn g&agrave;ng dễ di chuyển</h3>\r\n<p>Sở hữu thiết kế đặc trưng của d&ograve;ng&nbsp;<a title=\"Laptop Lenovo Ideapad S145 15IWL i3 (81MV00SXVN)\" href=\"https://www.thegioididong.com/laptop/lenovo-ideapad-s145\" target=\"_blank\" rel=\"noopener\">Lenovo IdeaPad S145</a>, laptop&nbsp;c&oacute; thiết kế đẹp tinh tế, lớp vỏ nhựa được phủ sơn m&agrave;u bạc khiến m&aacute;y c&oacute; ngoại h&igrave;nh sang trọng kh&ocirc;ng thua k&eacute;m những chiếc laptop kim loại nguy&ecirc;n khối. K&iacute;ch thước mỏng gọn c&ugrave;ng trọng lượng&nbsp;<strong>1.79 kg</strong>&nbsp;cho bạn thoải m&aacute;i x&ecirc; dịch.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-179kg.jpg\"><img class=\"lazy\" title=\"Thiết kế thanh lịch, gọn g&agrave;ng dễ di chuyển\" src=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-179kg.jpg\" alt=\"Thiết kế thanh lịch, gọn g&agrave;ng dễ di chuyển\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-179kg.jpg\" /></a></p>\r\n<h3>Hiển thị sắc n&eacute;t, chống ch&oacute;i</h3>\r\n<p>&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm Lenovo IdeaPad đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-lenovo-ideapad\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm Lenovo IdeaPad đang b&aacute;n tại Thegioididong.com\">Lenovo IdeaPad</a>&nbsp;trang bị m&agrave;n h&igrave;nh&nbsp;<strong><a title=\"Xem th&ecirc;m một số laptop m&agrave;n h&igrave;nh tr&ecirc;n 15 inch đang được kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-tren-15-inch\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m một số laptop m&agrave;n h&igrave;nh tr&ecirc;n 15 inch đang được kinh doanh tại Thegioididong.com\">15.6 inch</a>&nbsp;Full HD</strong>&nbsp;đem đến những h&igrave;nh ảnh sống động với m&agrave;u sắc ch&acirc;n thật, đẹp mắt, c&ocirc;ng nghệ Anti-Glare&nbsp;<strong>chống ch&oacute;i</strong>&nbsp;hiệu quả. B&ecirc;n cạnh đ&oacute;,&nbsp;<a title=\"Laptop Lenovo m&agrave;n h&igrave;nh mỏng được kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-lenovo-vien-man-hinh-mong\" target=\"_blank\" rel=\"noopener\">viền m&agrave;n h&igrave;nh mỏng</a>&nbsp;gi&uacute;p nh&igrave;n thoải m&aacute;i hơn.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-156inch.jpg\"><img class=\"lazy\" title=\"M&aacute;y t&iacute;nh trang bị m&agrave;n h&igrave;nh 15.6 inch Full HD\" src=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-156inch.jpg\" alt=\"M&aacute;y t&iacute;nh trang bị m&agrave;n h&igrave;nh 15.6 inch Full HD\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-156inch.jpg\" /></a></p>\r\n<h3>Cấu h&igrave;nh khỏe với CPU thế hệ mới</h3>\r\n<p>Với chip&nbsp;<strong>Intel&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c laptop Core i5 đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=core-i5\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c laptop Core i5 đang b&aacute;n tại Thegioididong.com\">Core i5</a>&nbsp;thế hệ 10</strong>,&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\">laptop</a>&nbsp;đem đến hiệu năng mạnh mẽ giải quyết mượt m&agrave; từ c&ocirc;ng việc văn ph&ograve;ng đến giải tr&iacute;.&nbsp;<a title=\"Laptop được trang bị RAM 8 GB đang kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=8-gb\" target=\"_blank\" rel=\"noopener\" type=\"Laptop được trang bị RAM 8 GB đang kinh doanh tại Thegioididong.com\"><strong>RAM 8 GB</strong></a>&nbsp;cho ph&eacute;p n&acirc;ng cấp l&ecirc;n 20 GB c&oacute; khả năng đa nhiệm nhanh, sử dụng chuy&ecirc;n nghiệp c&aacute;c ứng dụng đồ họa 2D.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-i5-10.jpg\"><img class=\"lazy\" title=\"Cấu h&igrave;nh khỏe với CPU thế hệ mới\" src=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-i5-10.jpg\" alt=\"Cấu h&igrave;nh khỏe với CPU thế hệ mới\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-i5-10.jpg\" /></a></p>\r\n<p><strong><a title=\"Laptop Lenovo ch&iacute;nh h&atilde;ng được trang bị ổ cứng SSD đang được kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-lenovo-o-cung-ssd\" target=\"_blank\" rel=\"noopener\" type=\"Laptop ch&iacute;nh h&atilde;ng được trang bị ổ cứng SSD đang được kinh doanh tại Thegioididong.com\">Ổ cứng SSD</a>&nbsp;512 GB</strong>&nbsp;kh&ocirc;ng chỉ đem đến dung lượng lưu trữ lớn m&agrave; c&ograve;n gi&uacute;p m&aacute;y phản hồi nhanh mọi thao t&aacute;c. Cụ thể, bạn chỉ mất khoảng 10-15 gi&acirc;y để mở m&aacute;y, 3-5 gi&acirc;y để mở ứng dụng.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-512gb.jpg\"><img class=\"lazy\" title=\"Ổ cứng SSD tốc độ nhanh\" src=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-512gb.jpg\" alt=\"Ổ cứng SSD tốc độ nhanh\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-512gb.jpg\" /></a></p>\r\n<p>Nhờ c&oacute; c&aacute;c cổng kết nối th&ocirc;ng dụng như USB 3.0, HDMI, USB 2.0, người d&ugrave;ng c&oacute; thể truyền v&agrave; nhập dữ liệu từ c&aacute;c thiết bị kh&aacute;c m&agrave; kh&ocirc;ng cần đến cổng chuyển đổi.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-usb.jpg\"><img class=\"lazy\" title=\"Cổng giao tiếp đa dạng\" src=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-usb.jpg\" alt=\"Cổng giao tiếp đa dạng\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-usb.jpg\" /></a></p>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Lenovo đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-lenovo\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Lenovo đang b&aacute;n tại Thegioididong.com\">Laptop Lenovo</a>&nbsp;IdeaPad sử dụng được khoảng&nbsp;<strong>4</strong>&nbsp;tiếng nếu sử dụng li&ecirc;n tục c&aacute;c t&aacute;c vụ văn ph&ograve;ng. Với thời lượng n&agrave;y, bạn c&oacute; thể y&ecirc;n t&acirc;m sử dụng cả ng&agrave;y m&agrave; kh&ocirc;ng sợ hết pin g&acirc;y gi&aacute;n đoạn c&ocirc;ng việc.&nbsp;&nbsp;</p>', 13260000, 'uploads/products/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-(2).jpg', 0, '2', '2021-09-26 17:00:00', NULL, 2, NULL),
(15, 'Laptop Asus VivoBook X515MA N4020/4GB/256GB/Win10', '<h2 dir=\"ltr\">Laptop Asus VivoBook X515MA (BR111T) l&agrave; một mẫu&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop học tập, văn ph&ograve;ng đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" rel=\"noopener\">laptop học tập - văn ph&ograve;ng</a>&nbsp;c&oacute; thiết kế gọn nhẹ, hiệu năng ổn định ph&ugrave; hợp với c&aacute;c bạn thường xuy&ecirc;n sử dụng c&aacute;c ứng dụng văn ph&ograve;ng nhẹ nh&agrave;ng.</h2>', '<h3 dir=\"ltr\">Cấu h&igrave;nh ổn định, xử l&yacute; tốt c&aacute;c ứng dụng văn ph&ograve;ng</h3>\r\n<p dir=\"ltr\"><a title=\"Xem th&ecirc;m c&aacute;c sản phẩm Asus VivoBook đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus-vivobook\" target=\"_blank\" rel=\"noopener\">Laptop Asus VivoBook</a>&nbsp;được trang bị con chip&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c laptop Intel Celeron/Pentium đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-celeron-pentium\" target=\"_blank\" rel=\"noopener\">Intel Celeron&nbsp;</a>c&oacute; hiệu năng ổn định xử l&yacute; c&aacute;c t&aacute;c vụ căn bản, với<strong>&nbsp;2 nh&acirc;n 2 luồng&nbsp;</strong>chip đạt xung nhịp&nbsp;<strong>1.10 GHz</strong>&nbsp;bạn lướt web, xem phim, nghe nhạc,... V&agrave; đạt xung nhịp tối đa l&agrave;<strong>&nbsp;2.8 GHz</strong>&nbsp;nhờ&nbsp;<strong>Turbo Boost&nbsp;</strong>để xử l&yacute; một số c&ocirc;ng việc văn ph&ograve;ng như: Word, Excel, Powerpoint,...</p>\r\n<p dir=\"ltr\">Đi c&ugrave;ng với chip l&agrave; card đồ họa t&iacute;ch hợp&nbsp;<strong>Intel UHD Graphics 600</strong>&nbsp;hỗ trợ việc xử l&yacute; h&igrave;nh ảnh tốt hơn v&agrave; tiết kiệm pin hiệu quả nhờ c&oacute; mức ti&ecirc;u thụ điện năng thấp n&ecirc;n th&iacute;ch hợp cho c&aacute;c t&aacute;c vụ văn ph&ograve;ng cơ bản.</p>\r\n<p dir=\"ltr\">M&aacute;y c&oacute;&nbsp;<a title=\"Laptop được trang bị RAM 4 GB đang kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" rel=\"noopener\"><strong>RAM 4 GB</strong></a>&nbsp;cho khả năng xử l&yacute; đa nhiệm ổn định, bạn c&oacute; thể mở c&ugrave;ng l&uacute;c nhiều ứng dụng để phục vụ cho c&ocirc;ng việc tốt hơn m&agrave; kh&ocirc;ng lo m&aacute;y bị đơ hay giật. Nếu 4 GB c&oacute; thể l&agrave; qu&aacute; &iacute;t với bạn th&igrave; m&aacute;y c&ograve;n hỗ trợ n&acirc;ng cấp RAM l&ecirc;n&nbsp;<strong>16 GB</strong>&nbsp;gi&uacute;p bạn dễ d&agrave;ng n&acirc;ng cấp cho ph&ugrave; hợp với nhu cầu sử dụng.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/233852/asus-vivobook-x515ma-n4020-br111t-151221-101256.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook X515MA (BR111T) - Hiệu năng\" src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151221-101256.jpg\" alt=\"Laptop Asus VivoBook X515MA (BR111T) - Hiệu năng\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151221-101256.jpg\" /></a></p>\r\n<p dir=\"ltr\">Thời gian khởi động m&aacute;y nhanh ch&oacute;ng v&agrave; khả năng lưu trữ lớn nhờ ổ cứng&nbsp;<strong><a title=\"Xem th&ecirc;m c&aacute;c laptop c&oacute; SSD 256 GB\" href=\"https://www.thegioididong.com/laptop?g=ssd-256-gb\" target=\"_blank\" rel=\"noopener\">SSD 256 GB</a>&nbsp;</strong>tr&ecirc;n laptop. Bạn chỉ cần mất khoảng 10 - 15 gi&acirc;y để khởi động laptop v&agrave; 3 &ndash; 5 gi&acirc;y để khởi động c&aacute;c ứng dụng văn ph&ograve;ng, giải tr&iacute;, thỏa sức lưu trữ mọi dữ liệu cần thiết cho c&ocirc;ng việc, học tập hay những bộ phim y&ecirc;u th&iacute;ch để xem khi rảnh.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/233852/asus-vivobook-x515ma-n4020-br111t-151321-101347.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook X515MA (BR111T) - SSD\" src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151321-101347.jpg\" alt=\"Laptop Asus VivoBook X515MA (BR111T) - SSD\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151321-101347.jpg\" /></a></p>', 6890000, 'uploads/products/t-600x400.jpg', 64, '2', '2021-09-29 17:00:00', '2021-10-18 03:11:58', 2, NULL),
(16, 'Laptop HP Pavilion 15 eg0070TU i5 1135G7/8GB/512GB/Office H&S2019/Win10', '<h2 dir=\"ltr\"><a title=\"Laptop HP Pavilion 15 eg0070TU i5 (2L9H3PA)\" href=\"https://www.thegioididong.com/laptop/hp-pavilion-15-eg0070tu-i5-2l9h3pa\" target=\"_blank\" rel=\"noopener\">Laptop HP Pavilion 15 eg0070TU i5 1135G7</a>&nbsp;mang đến hiệu năng ổn định nhờ sở hữu con chip Intel Core i5 gen 11, xử l&yacute; tốt c&aacute;c c&ocirc;ng việc văn ph&ograve;ng v&agrave; đồ họa cơ bản. Đ&acirc;y sẽ l&agrave; người bạn đồng h&agrave;nh tốt nhất của bạn trong c&ocirc;ng việc v&agrave; học tập.</h2>', '<h3 dir=\"ltr\">Cấu h&igrave;nh mạnh mẽ, xử l&yacute; trơn tru mọi c&ocirc;ng việc</h3>\r\n<p dir=\"ltr\">Chiếc<a title=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\">&nbsp;laptop&nbsp;</a>n&agrave;y được trang bị con chip&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c laptop Core i5 đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=core-i5\" target=\"_blank\" rel=\"noopener\">Intel Core i5&nbsp;</a>Tiger Lake&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c laptop Intel Gen 11 đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-cpu-intel-gen-11\" target=\"_blank\" rel=\"noopener\">thế hệ 11</a>&nbsp;mới nhất hiện nay, con chip c&oacute; 4 nh&acirc;n 8 luồng cho xung nhịp trung b&igrave;nh l&agrave;&nbsp;<strong>&nbsp;2.40 GHz</strong>&nbsp;khi xử l&yacute; c&aacute;c t&aacute;c vụ nhẹ nh&agrave;ng như l&agrave;m việc với Word hay xem phim, lướt web giải tr&iacute; v&agrave; đạt xung nhịp tối đa l&ecirc;n đến<strong>&nbsp;4.2 GHz</strong>&nbsp;nhờ c&ocirc;ng nghệ Turbo Boost để xử l&yacute; c&aacute;c t&aacute;c vụ nặng hơn như Photoshop, render video,...</p>\r\n<p dir=\"ltr\">B&ecirc;n cạnh đ&oacute; chip Intel Core i5 c&ograve;n sử dụng l&otilde;i&nbsp;<strong>Willow Cove&nbsp;</strong>gi&uacute;p ti&ecirc;u tốn &iacute;t năng lượng hơn, k&eacute;o d&agrave;i thời lượng pin l&agrave;m việc hiệu quả hơn.</p>\r\n<p dir=\"ltr\">Card t&iacute;ch hợp Intel Iris Xe Graphics mang đến h&igrave;nh ảnh rực rỡ, cho hiệu năng đồ họa cao. Khi card đồ họa n&agrave;y kết hợp c&ugrave;ng chip gen 11 sẽ đem đến khả năng chơi được c&aacute;c tựa game như Children of Morta, Corruption 2029, Little Nightmares,... ở mức đồ họa trung b&igrave;nh, kh&aacute;.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/233627/hp-pavilion-15-eg0070tu-i5-2l9h3pa-164821-034841.jpg\"><img class=\"lazy\" title=\"Laptop HP Pavilion 15 eg0070TU i5 (1135G7) - Intel\" src=\"https://cdn.tgdd.vn/Products/Images/44/233627/hp-pavilion-15-eg0070tu-i5-2l9h3pa-164821-034841.jpg\" alt=\"Laptop HP Pavilion 15 eg0070TU i5 (1135G7) - Intel\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/233627/hp-pavilion-15-eg0070tu-i5-2l9h3pa-164821-034841.jpg\" /></a></p>\r\n<p dir=\"ltr\">Trang bị<strong>&nbsp;<a title=\"Laptop được trang bị RAM 8 GB đang kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-8-gb\" target=\"_blank\" rel=\"noopener\">RAM 8 GB</a></strong>&nbsp;c&oacute;<strong>&nbsp;tốc độ Bus RAM</strong>&nbsp;l&ecirc;n đến&nbsp;<strong>3200 MHz</strong>&nbsp;đem đến khả năng đa nhiệm tốt, bạn c&oacute; thể mở c&ugrave;ng l&uacute;c nhiều ứng dụng m&agrave; vẫn kh&ocirc;ng bị giảm tốc độ xử l&yacute; của m&aacute;y, bạn c&oacute; thể vừa mở Photoshop để chỉnh sửa h&igrave;nh ảnh vừa mở Google để tra cứu th&ocirc;ng tin m&agrave; kh&ocirc;ng lo m&aacute;y bị đứng hay phản hồi chậm. Laptop HP c&ograve;n hỗ trợ&nbsp;<strong>32 GB</strong>&nbsp;gi&uacute;p dễ d&agrave;ng n&acirc;ng cấp khi cần, phục vụ c&ocirc;ng việc tốt hơn.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/233627/hp-pavilion-15-eg0070tu-i5-2l9h3pa-164821-034848.jpg\"><img class=\"lazy\" title=\"Laptop HP Pavilion 15 eg0070TU i5 (1135G7) - RAM\" src=\"https://cdn.tgdd.vn/Products/Images/44/233627/hp-pavilion-15-eg0070tu-i5-2l9h3pa-164821-034848.jpg\" alt=\"Laptop HP Pavilion 15 eg0070TU i5 (1135G7) - RAM\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/233627/hp-pavilion-15-eg0070tu-i5-2l9h3pa-164821-034848.jpg\" /></a></p>\r\n<p dir=\"ltr\">Sở hữu ổ cứng&nbsp;<strong><a title=\"https://www.thegioididong.com/laptop?g=ssd-512-gbXem th&ecirc;m c&aacute;c laptop c&oacute; SSD 512 GB\" href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" rel=\"noopener\">SSD 512</a>&nbsp;GB M.2 PCIe</strong>&nbsp;kh&ocirc;ng chỉ cho tốc độ đọc, ghi dữ liệu nhanh hơn ổ cứng HDD m&agrave; con cho thời gian khởi động m&aacute;y v&agrave; v&agrave;o ứng dụng chỉ mất khoảng v&agrave;i gi&acirc;y. Dung lượng&nbsp;<strong>512 GB</strong>&nbsp;cho kh&ocirc;ng gian lưu trữ rộng r&atilde;i, bạn c&oacute; thể thoải m&aacute;i lưu trữ t&agrave;i liệu học tập hay những bộ phim y&ecirc;u th&iacute;ch m&agrave; kh&ocirc;ng lo ổ cứng bị đầy l&agrave;m giảm tốc độ m&aacute;y.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/233627/hp-pavilion-15-eg0070tu-i5-2l9h3pa-164821-034856.jpg\"><img class=\"lazy\" title=\"Laptop HP Pavilion 15 eg0070TU i5 (1135G7) - SSD\" src=\"https://cdn.tgdd.vn/Products/Images/44/233627/hp-pavilion-15-eg0070tu-i5-2l9h3pa-164821-034856.jpg\" alt=\"Laptop HP Pavilion 15 eg0070TU i5 (1135G7) - SSD\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/233627/hp-pavilion-15-eg0070tu-i5-2l9h3pa-164821-034856.jpg\" /></a></p>', 17590000, 'uploads/products/img-pro-05.png', 53, '2', '2021-09-01 17:00:00', '2021-10-18 03:12:18', 2, NULL);
INSERT INTO `products` (`id`, `name`, `desc`, `content`, `price`, `thumbnail`, `qty`, `status`, `created_at`, `updated_at`, `cat_id`, `deleted_at`) VALUES
(17, 'Máy tính bảng iPad Pro 11 inch Wifi 128GB (2020)', '<h2><a title=\"Tham khảo gi&aacute; m&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 ch&iacute;nh h&atilde;ng tại thegioididong.com\" href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-11-inch-2020\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; m&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 ch&iacute;nh h&atilde;ng tại thegioididong.com\">iPad Pro 11 inch 2020</a>&nbsp;l&agrave; mẫu&nbsp;<a title=\"Tham khảo gi&aacute; c&aacute;c mẫu m&aacute;y t&iacute;nh bảng Apple iPad ch&iacute;nh h&atilde;ng tại thegioididong.com\" href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; c&aacute;c mẫu m&aacute;y t&iacute;nh bảng Apple iPad ch&iacute;nh h&atilde;ng tại thegioididong.com\">iPad</a>&nbsp;mới nhất vừa được&nbsp;<a title=\"Tham khảo sản phẩm Apple ch&iacute;nh h&atilde;ng tại Thegioididong.com \" href=\"https://www.thegioididong.com/apple\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; điện thoại smartphone iPhone ch&iacute;nh h&atilde;ng\">Apple</a>&nbsp;ra mắt v&agrave;o 18/3 với thiết kế gần như kh&ocirc;ng thay đổi so với thế hệ trước, chủ yếu l&agrave; sự n&acirc;ng cấp đến từ chip A12Z cho hiệu năng mạnh mẽ v&agrave; cụm camera c&oacute; cảm biến mới hỗ trợ tăng cường thực tế ảo.</h2>', '<h3>Cấu h&igrave;nh khủng vượt giới hạn của một chiếc tablet</h3>\r\n<p>Được Apple trang bị vi xử l&yacute; A12Z Bionic mới nhất, iPad Pro 11 inch c&oacute; một tốc độ vượt trội, c&oacute; thể so s&aacute;nh được với c&aacute;c mẫu&nbsp;<a title=\"Tham khảo gi&aacute; laptop m&aacute;y t&iacute;nh x&aacute;ch tay ch&iacute;nh h&atilde;ng tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; laptop m&aacute;y t&iacute;nh x&aacute;ch tay ch&iacute;nh h&atilde;ng tại thegioididong.com\">m&aacute;y t&iacute;nh x&aacute;ch tay</a>&nbsp;hiện tại.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-1.jpg\"><img class=\"lazy\" title=\"M&aacute;y t&iacute;nh bảng iPad Pro 2020 c&oacute; một cấu h&igrave;nh khủng gần như tương đương PC\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-1.jpg\" alt=\"M&aacute;y t&iacute;nh bảng iPad Pro 2020 c&oacute; một cấu h&igrave;nh khủng gần như tương đương PC\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-1.jpg\" /></a></p>\r\n<p>Mọi ứng dụng hoạt động tr&ecirc;n iPad trước vốn đ&atilde; mượt, nay c&ograve;n nhanh v&agrave; tr&ocirc;i chảy hơn, từ&nbsp;c&aacute;c t&aacute;c vụ đơn giản h&agrave;ng ng&agrave;y cho đến c&aacute;c c&ocirc;ng việc chuy&ecirc;n s&acirc;u về đồ họa đều được xử l&yacute; nhanh ch&oacute;ng.</p>\r\n<p>Apple cũng đ&atilde; tăng cường đến 8 l&otilde;i&nbsp;xử l&yacute; đồ họa, gi&uacute;p cho iPad Pro 2020 trở th&agrave;nh một chiếc&nbsp;<a title=\"Tham khảo m&aacute;y t&iacute;nh bảng ch&iacute;nh h&atilde;ng gi&aacute; tốt tại thegioididong.com\" href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo m&aacute;y t&iacute;nh bảng ch&iacute;nh h&atilde;ng gi&aacute; tốt tại thegioididong.com\">m&aacute;y t&iacute;nh bảng</a>&nbsp;đ&aacute;p ứng tuyệt vời cho nhu cầu đồ họa, giải tr&iacute; v&agrave; chơi game.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-3.jpg\"><img class=\"lazy\" title=\"M&aacute;y t&iacute;nh bảng Apple iPad Pro 11 inch 2020 sở hữu hiệu năng mạnh mẽ\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-3.jpg\" alt=\"M&aacute;y t&iacute;nh bảng Apple iPad Pro 11 inch 2020 sở hữu hiệu năng mạnh mẽ\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-3.jpg\" /></a></p>\r\n<p>iPad&nbsp;Pro 11 inch 2020 vẫn duy tr&igrave; sử dụng m&agrave;n h&igrave;nh Liquid Retina c&ugrave;ng c&aacute;c t&iacute;nh năng h&agrave;ng đầu như ProMotion, tần số qu&eacute;t l&ecirc;n đến 120 Hz, True Tone, gi&uacute;p cho iPad Pro trở th&agrave;nh một trong những thiết bị di động sở hữu m&agrave;n h&igrave;nh tốt nhất hiện nay (3/2020).</p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"T&igrave;m hiểu về c&ocirc;ng nghệ m&agrave;n h&igrave;nh Liquid Retina của Apple\" href=\"https://www.thegioididong.com/hoi-dap/cung-tim-hieu-ve-man-hinh-liquid-retina-tren-iphon-1125106\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu về c&ocirc;ng nghệ m&agrave;n h&igrave;nh Liquid Retina của Apple\">C&ugrave;ng t&igrave;m hiểu về m&agrave;n h&igrave;nh Liquid Retina tr&ecirc;n iPhone XR</a></p>\r\n<p>Chất lượng hiển thị tr&ecirc;n iPad Pro 2020 l&agrave; cực kỳ xuất sắc, dải m&agrave;u rộng hiển thị chuẩn x&aacute;c nhằm tối ưu cho việc chỉnh sửa đồ họa chuy&ecirc;n nghiệp, độ s&aacute;ng cao 600 nit đ&atilde; gi&uacute;p bạn c&oacute; thể sử dụng m&aacute;y ngo&agrave;i trời một c&aacute;ch dễ d&agrave;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-2020-1.jpg\"><img class=\"lazy\" title=\"M&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 | M&agrave;n h&igrave;nh Liquid Retina\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-1.jpg\" alt=\"M&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 | M&agrave;n h&igrave;nh Liquid Retina\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-1.jpg\" /></a></p>\r\n<h3>Camera đa t&iacute;nh năng, hỗ trợ AR tốt hơn</h3>\r\n<p>Sự thay đổi&nbsp;về ngoại h&igrave;nh duy nhất của&nbsp;iPad Pro 2020 c&oacute; lẽ l&agrave; cụm camera sau h&igrave;nh vu&ocirc;ng ở mặt lưng với thiết kế tương tự d&ograve;ng iPhone 11. M&ocirc;-đun camera n&agrave;y chứa 3 cảm biến, gồm camera ch&iacute;nh g&oacute;c rộng 12 MP v&agrave; camera g&oacute;c si&ecirc;u rộng 10 MP v&agrave; cảm biến mới c&oacute; t&ecirc;n&nbsp;LiDAR.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-2020-2.jpg\"><img class=\"lazy\" title=\"M&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 với cụm camera sau h&igrave;nh vu&ocirc;ng\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-2.jpg\" alt=\"M&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 với cụm camera sau h&igrave;nh vu&ocirc;ng\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-2.jpg\" /></a></p>\r\n<p>Camera ch&iacute;nh 12 MP hỗ trợ chụp những tấm ảnh thường ng&agrave;y r&otilde; n&eacute;t. Trong khi đ&oacute;, ống k&iacute;nh Ultra Wide 10 MP tăng cường độ mở cho g&oacute;c chụp si&ecirc;u rộng ấn tượng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-7.jpg\"><img class=\"lazy\" title=\"Chụp h&igrave;nh v&agrave; chỉnh sửa nhanh ch&oacute;ng với M&aacute;y t&iacute;nh bảng iPad Pro 2020 11 inch\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-7.jpg\" alt=\"Chụp h&igrave;nh v&agrave; chỉnh sửa nhanh ch&oacute;ng với M&aacute;y t&iacute;nh bảng iPad Pro 2020 11 inch\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-7.jpg\" /></a></p>\r\n<p>Với giao diện camera tối giản, dễ d&ugrave;ng tr&ecirc;n iPad, bạn c&oacute; thể chụp to&agrave;n bộ căn ph&ograve;ng trong một lần chụp bằng camera Ultra Wide v&agrave; chuyển đến ứng dụng để thiết kế ngay v&agrave; lưu lại t&aacute;c phẩm của m&igrave;nh với những thao t&aacute;c cực kỳ đơn giản, nhanh ch&oacute;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-inch-2020.gif\"><img class=\"lazy\" title=\"M&aacute;y t&iacute;nh bảng iPad Pro 2020 11 inch | Chế độ VR mượt m&agrave; nhờ cảm biến LiDAR\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-inch-2020.gif\" alt=\"M&aacute;y t&iacute;nh bảng iPad Pro 2020 11 inch | Chế độ VR mượt m&agrave; nhờ cảm biến LiDAR\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-inch-2020.gif\" /></a></p>\r\n<p>Hệ thống camera&nbsp;<a title=\"Tham khảo iPad Pro ch&iacute;nh h&atilde;ng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-pro\" target=\"_blank\" rel=\"noopener\">iPad Pro</a>&nbsp;vận h&agrave;nh linh hoạt đến kh&ocirc;ng ngờ. Từ việc quay, chỉnh sửa cho đến chia sẻ video 4K, tất cả thật dễ d&agrave;ng tr&ecirc;n c&ugrave;ng một thiết bị. Bạn cũng c&oacute; thể quay video ho&agrave;n hảo với micro đạt chuẩn ph&ograve;ng thu, ph&aacute;t &acirc;m thanh 4 k&ecirc;nh, l&agrave;m cho phần nghe của thước phim trở n&ecirc;n sống động hơn.</p>\r\n<p>Cảm biến LiDAR (Light Detection and Ranging) l&agrave; một c&ocirc;ng nghệ h&igrave;nh ảnh mới, lần đầu ti&ecirc;n được Apple t&iacute;ch hợp tr&ecirc;n thiết bị của m&igrave;nh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-8.jpg\"><img class=\"lazy\" title=\"Cảm biến LiDAR tr&ecirc;n iPad Pro 11 inch 2020 hỗ trợ thực tế ảo mạnh mẽ hơn\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-8.jpg\" alt=\"Cảm biến LiDAR tr&ecirc;n iPad Pro 11 inch 2020 hỗ trợ thực tế ảo mạnh mẽ hơn\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-8.jpg\" /></a></p>\r\n<p>Cảm biến n&agrave;y hoạt động dựa tr&ecirc;n nguy&ecirc;n l&yacute; thời gian phản xạ của &aacute;nh s&aacute;ng đến vật thể để phục vụ đo chiều s&acirc;u, t&iacute;nh to&aacute;n khoảng c&aacute;ch một c&aacute;ch ch&iacute;nh x&aacute;c hỗ trợ đắc lực cho hoạt động của c&aacute;c ứng dụng AR.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-2020-3.jpg\"><img class=\"lazy\" title=\"Camera TrueDepth ở mặt trước m&aacute;y t&iacute;nh bảng iPad Pro 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-3.jpg\" alt=\"Camera TrueDepth ở mặt trước m&aacute;y t&iacute;nh bảng iPad Pro 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-3.jpg\" /></a></p>\r\n<p>Camera TrueDepth ở mặt trước sẽ hỗ trợ nhận diện khu&ocirc;n mặt 3D Face ID,&nbsp;chụp ảnh ch&acirc;n dung. Tr&ograve; chuyện với bạn b&egrave; qua FaceTime sẽ trở n&ecirc;n th&uacute; vị hơn với&nbsp;t&iacute;nh năng Animoji vui nhộn.</p>\r\n<h3>Thiết kế quen thuộc, vẫn bền bỉ v&agrave; đẳng cấp</h3>', 21190000, 'uploads/products/ipad-pro-11-inch-2020-bac-600x600-1-600x600.jpg', 3, '1', '2021-10-13 03:49:32', '2021-10-18 02:48:23', 8, '2021-10-18 02:48:23'),
(18, 'iPhone Vivo V20', '<h3>Chiếc đi&ecirc;̣n thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-a15s\">OPPO A15s</a>&nbsp;là m&ocirc;̣t phi&ecirc;n bản n&acirc;ng cấp của&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-a15\">OPPO A15</a>, có c&acirc;́u hình &acirc;́n tượng trong t&acirc;̀m giá đủ đ&ecirc;̉ người dùng thỏa sức làm vi&ecirc;̣c v&agrave; giải trí khi c&acirc;̀n thi&ecirc;́t.</h3>', '<h3>Hiệu năng vượt xa mọi giới hạn</h3>\r\n<p>Apple đ&atilde; trang bị con chip mới nhất của h&atilde;ng (t&iacute;nh đến 11/2020) cho iPhone 12 đ&oacute; l&agrave;&nbsp;<a title=\"T&igrave;m hiểu chip A14 Bionic l&agrave; g&igrave;?\" href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-a14-bionic-tren-iphone-12-va-ipad-1290695\" target=\"_blank\" rel=\"noopener\">A14 Bionic</a>, được sản xuất tr&ecirc;n tiến tr&igrave;nh 5 nm với hiệu suất ổn định hơn so với chip A13 được trang bị tr&ecirc;n phi&ecirc;n bản tiền nhiệm&nbsp;<a title=\"Tham khảo điện thoại iPhone 11 ch&iacute;nh h&atilde;ng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/iphone-11\" target=\"_blank\" rel=\"noopener\">iPhone 11</a>.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/213031/iphone-12-144220-044259.jpg\"><img class=\"lazy\" title=\"Chip A14 Bionic mạnh mẽ | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-144220-044259.jpg\" alt=\"Chip A14 Bionic mạnh mẽ | iPhone 12\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-144220-044259.jpg\" /></a></p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"T&igrave;m hiểu về chip Apple A14 Bionic tr&ecirc;n iPhone 12 v&agrave; iPad Air 2020\" href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-a14-bionic-tren-iphone-12-va-ipad-1290695\" target=\"_blank\" rel=\"noopener\">T&igrave;m hiểu về chip Apple A14 Bionic tr&ecirc;n iPhone 12 v&agrave; iPad Air 2020</a></p>\r\n<p>Với CPU Apple A14 Bionic, bạn c&oacute; thể dễ d&agrave;ng trải nghiệm mọi tựa game với những pha chuyển cảnh mượt m&agrave; hay h&agrave;ng loạt hiệu ứng đồ họa tuyệt đẹp ở mức đồ họa cao m&agrave; kh&ocirc;ng lo t&igrave;nh trạng giật lag.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/213031/iphone-12-18.jpg\"><img class=\"lazy\" title=\"Chiến game si&ecirc;u mượt, đồ họa tuyệt đẹp | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-18.jpg\" alt=\"Chiến game si&ecirc;u mượt, đồ họa tuyệt đẹp | iPhone 12\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-18.jpg\" /></a></p>\r\n<p>Chưa hết, Apple c&ograve;n g&acirc;y bất ngờ đến người d&ugrave;ng với hệ thống 5G lần đầu ti&ecirc;n được trang bị tr&ecirc;n những chiếc&nbsp;<a title=\"Tham khảo iPhone kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" rel=\"noopener\">iPhone</a>, cho tốc độ truyền tải dữ liệu nhanh hơn, ổn định hơn.</p>', 11190000, 'uploads/products/xiaomi-redmi-9t-6gb-110621-080650-600x600.jpg', 12, '1', '2021-10-18 02:53:59', '2021-10-18 02:53:59', 11, NULL),
(19, 'iPhone Samsung Galaxy M51', '<h2><a title=\"Tham khảo th&ecirc;m về c&aacute;c d&ograve;ng điện thoại Samsung tr&ecirc;n thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo th&ecirc;m về c&aacute;c d&ograve;ng điện thoại Samsung tr&ecirc;n thegioididong.com\">Samsung</a>&nbsp;lại tiếp tục cho ra mắt chiếc smartphone mới thuộc thế hệ&nbsp;<a title=\"Tham khảo th&ecirc;m c&aacute;c điện thoại d&ograve;ng Galaxy M tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-m\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo th&ecirc;m c&aacute;c điện thoại d&ograve;ng Galaxy M tại thegioididong.com\">Galaxy M</a>&nbsp;với t&ecirc;n gọi l&agrave;&nbsp;<a title=\"Tham khảo gi&aacute; b&aacute;n điện thoại Samsung Galaxy M51 b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-m51\">Samsung&nbsp;Galaxy M51</a>. Thiết kế mới n&agrave;y tuy nằm trong ph&acirc;n kh&uacute;c tầm trung nhưng được Samsung n&acirc;ng cấp v&agrave; cải tiến với camera g&oacute;c si&ecirc;u rộng, dung lượng pin si&ecirc;u khủng c&ugrave;ng vẻ ngo&agrave;i sang trọng v&agrave; thời thượng.</h2>', '<h3>Thiết kế hiện đại v&agrave; đẳng cấp.</h3>\r\n<p>Ấn tượng ban đầu với&nbsp;<a title=\"Tham khảo th&ecirc;m c&aacute;c d&ograve;ng smartphone c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh từ 6 inch trở l&ecirc;n tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-tu-6-inch\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo th&ecirc;m c&aacute;c d&ograve;ng smartphone c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh từ 6 inch trở l&ecirc;n tại thegioididong.com\">m&agrave;n h&igrave;nh</a>&nbsp;của Galaxy M51 l&agrave; kiểu m&agrave;n h&igrave;nh Infinity-O rộng 6.7 inch. Kiểu thiết kế n&agrave;y đưa camera selfie thu gọn hơn chỉ bằng một h&igrave;nh tr&ograve;n nhỏ c&ugrave;ng thiết kế&nbsp;<a title=\"Tham khảo th&ecirc;m c&aacute;c d&ograve;ng smartphone c&oacute; m&agrave;n h&igrave;nh tr&agrave;n viền tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-man-hinh-tran-vien\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo th&ecirc;m c&aacute;c d&ograve;ng smartphone c&oacute; m&agrave;n h&igrave;nh tr&agrave;n viền tại thegioididong.com\">m&agrave;n h&igrave;nh tr&agrave;n viền</a>&nbsp;l&agrave;m tăng khả năng hiển thị h&igrave;nh ảnh hơn.</p>\r\n<p>Ngo&agrave;i ra, m&aacute;y c&ograve;n sở hữu c&ocirc;ng nghệ m&agrave;n h&igrave;nh Super AMOLED Plus&nbsp;mang đến chất lượng hiển thị sắc n&eacute;t, h&igrave;nh ảnh tươi tắn cho bạn tận hưởng c&aacute;c chương tr&igrave;nh giải tr&iacute; hấp dẫn, thưởng thức c&aacute;c bộ phim bom tấn, chơi những tựa game y&ecirc;u th&iacute;ch v&ocirc; c&ugrave;ng bắt mắt.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/217536/samsung-galaxy-m51-274020-094023.jpg\"><img class=\"lazy\" title=\"Sở hữu m&agrave;n h&igrave;nh tr&agrave;n viền với độ ph&acirc;n giải cao | Samsung Galaxy M51\" src=\"https://cdn.tgdd.vn/Products/Images/42/217536/samsung-galaxy-m51-274020-094023.jpg\" alt=\"Sở hữu m&agrave;n h&igrave;nh tr&agrave;n viền với độ ph&acirc;n giải cao | Samsung Galaxy M51\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/217536/samsung-galaxy-m51-274020-094023.jpg\" /></a></p>\r\n<p>Mặt lưng của m&aacute;y được thiết kế m&agrave;u gradient, chất liệu vỏ l&agrave;m bằng nhựa n&ecirc;n m&aacute;y rất nhẹ chỉ c&oacute; trọng lượng 213 g v&agrave; với độ mỏng 9.5 mm. B&ecirc;n cạnh đ&oacute;, c&aacute;c cạnh của m&aacute;y bo cong &ocirc;m s&aacute;t phần khung viền mang lại cảm gi&aacute;c vừa chắc chắn m&agrave; rất nhẹ nh&agrave;ng khi cầm nắm tr&ecirc;n tay.</p>\r\n<p>&nbsp;</p>', 8490000, 'uploads/products/iphone-12-pro-256gb-(2).jpg', 23, '1', '2021-10-18 03:00:25', '2021-10-18 03:00:25', 1, NULL),
(20, 'iPhone 12 64GB', '<h3>Trong những th&aacute;ng cuối năm 2020&nbsp;<a title=\"Tham khảo sản phẩm ch&iacute;nh h&atilde;ng của Apple tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/apple\" target=\"_blank\" rel=\"noopener\">Apple</a>&nbsp;đ&atilde; ch&iacute;nh thức giới thiệu đến người d&ugrave;ng cũng như iFan thế hệ iPhone&nbsp;12&nbsp;series&nbsp;mới với h&agrave;ng loạt t&iacute;nh năng bức ph&aacute;, thiết kế được lột x&aacute;c ho&agrave;n to&agrave;n, hiệu năng đầy mạnh mẽ v&agrave; một trong số đ&oacute; ch&iacute;nh l&agrave;&nbsp;<a title=\"Tham khảo điện thoại iPhone 12 ch&iacute;nh h&atilde;ng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/iphone-12\" target=\"_blank\" rel=\"noopener\">iPhone 12 64GB</a>.</h3>', '<h3>Hiệu năng vượt xa mọi giới hạn</h3>\r\n<p>Apple đ&atilde; trang bị con chip mới nhất của h&atilde;ng (t&iacute;nh đến 11/2020) cho iPhone 12 đ&oacute; l&agrave;&nbsp;<a title=\"T&igrave;m hiểu chip A14 Bionic l&agrave; g&igrave;?\" href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-a14-bionic-tren-iphone-12-va-ipad-1290695\" target=\"_blank\" rel=\"noopener\">A14 Bionic</a>, được sản xuất tr&ecirc;n tiến tr&igrave;nh 5 nm với hiệu suất ổn định hơn so với chip A13 được trang bị tr&ecirc;n phi&ecirc;n bản tiền nhiệm&nbsp;<a title=\"Tham khảo điện thoại iPhone 11 ch&iacute;nh h&atilde;ng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/iphone-11\" target=\"_blank\" rel=\"noopener\">iPhone 11</a>.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/213031/iphone-12-144220-044259.jpg\"><img class=\"lazy\" title=\"Chip A14 Bionic mạnh mẽ | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-144220-044259.jpg\" alt=\"Chip A14 Bionic mạnh mẽ | iPhone 12\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-144220-044259.jpg\" /></a></p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"T&igrave;m hiểu về chip Apple A14 Bionic tr&ecirc;n iPhone 12 v&agrave; iPad Air 2020\" href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-a14-bionic-tren-iphone-12-va-ipad-1290695\" target=\"_blank\" rel=\"noopener\">T&igrave;m hiểu về chip Apple A14 Bionic tr&ecirc;n iPhone 12 v&agrave; iPad Air 2020</a></p>\r\n<p>Với CPU Apple A14 Bionic, bạn c&oacute; thể dễ d&agrave;ng trải nghiệm mọi tựa game với những pha chuyển cảnh mượt m&agrave; hay h&agrave;ng loạt hiệu ứng đồ họa tuyệt đẹp ở mức đồ họa cao m&agrave; kh&ocirc;ng lo t&igrave;nh trạng giật lag.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/213031/iphone-12-18.jpg\"><img class=\"lazy\" title=\"Chiến game si&ecirc;u mượt, đồ họa tuyệt đẹp | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-18.jpg\" alt=\"Chiến game si&ecirc;u mượt, đồ họa tuyệt đẹp | iPhone 12\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-18.jpg\" /></a></p>\r\n<p>Chưa hết, Apple c&ograve;n g&acirc;y bất ngờ đến người d&ugrave;ng với hệ thống 5G lần đầu ti&ecirc;n được trang bị tr&ecirc;n những chiếc&nbsp;<a title=\"Tham khảo iPhone kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" rel=\"noopener\">iPhone</a>, cho tốc độ truyền tải dữ liệu nhanh hơn, ổn định hơn.</p>', 23490000, 'uploads/products/iphone-12-(2).jpg', 23, '1', '2021-10-18 03:02:47', '2021-10-18 03:02:47', 1, NULL),
(21, 'iPhone OPPO A15s', '<h3>Chiếc đi&ecirc;̣n thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-a15s\">OPPO A15s</a>&nbsp;là m&ocirc;̣t phi&ecirc;n bản n&acirc;ng cấp của&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-a15\">OPPO A15</a>, có c&acirc;́u hình &acirc;́n tượng trong t&acirc;̀m giá đủ đ&ecirc;̉ người dùng thỏa sức làm vi&ecirc;̣c v&agrave; giải trí khi c&acirc;̀n thi&ecirc;́t.</h3>', '<h3 dir=\"ltr\">Thiết kế hiện đại, vẻ ngoài cá tính</h3>\r\n<p dir=\"ltr\">Nh&igrave;n chung, thi&ecirc;́t k&ecirc;́ của OPPO A15s v&acirc;̃n mang đến cho người d&ugrave;ng cảm gi&aacute;c mạnh mẽ, cá tính nhưng cũng kh&ocirc;ng k&eacute;m phần sang trọng, mỏng chỉ 7.9 mm với mặt&nbsp;<a title=\"Tham khảo th&ocirc;ng tin k&iacute;nh cường lực Corning Gorilla Glass 3 tại thegioididong.com\" href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cac-mat-kinh-cuong-luc-gorilla-glass-1172198#glass3\" target=\"_blank\" rel=\"noopener\">kính cường lực&nbsp;Corning Gorilla Glass 3</a>&nbsp;tạo nét sang trọng cho chi&ecirc;́c máy.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/229948/oppo-a15s-1.jpg\"><img class=\"lazy\" title=\"Điện thoại OPPO A15s | Thiết kế hiện đại, vẻ ngo&agrave;i c&aacute; t&iacute;nh\" src=\"https://cdn.tgdd.vn/Products/Images/42/229948/oppo-a15s-1.jpg\" alt=\"Điện thoại OPPO A15s | Thiết kế hiện đại, vẻ ngo&agrave;i c&aacute; t&iacute;nh\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/229948/oppo-a15s-1.jpg\" /></a></p>\r\n<p dir=\"ltr\">Điểm nổi bật ăn tiền tr&ecirc;n OPPO A15s ch&iacute;nh l&agrave; phần mặt lưng nhựa cao cấp mang đến một xu hướng trẻ trung, thanh lịch kh&ocirc;ng qu&aacute; m&agrave;u m&egrave;, b&oacute;ng bẩy như c&aacute;c sản phẩm gi&aacute; rẻ kh&aacute;c.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/229948/oppo-a15s-253721-103736.jpg\"><img class=\"lazy\" title=\"Điện thoại OPPO A15s | Thiết kế mặt lưng\" src=\"https://cdn.tgdd.vn/Products/Images/42/229948/oppo-a15s-253721-103736.jpg\" alt=\"Điện thoại OPPO A15s | Thiết kế mặt lưng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/229948/oppo-a15s-253721-103736.jpg\" /></a></p>\r\n<p dir=\"ltr\">Ngo&agrave;i ra, phần mặt lưng của m&aacute;y c&ograve;n được l&agrave;m cong 3D kết hợp c&ugrave;ng việc bo tr&ograve;n c&aacute;c cạnh viền mang đến trải nghiệm cầm nắm &ecirc;m &aacute;i, chắc chắn v&agrave; rất vừa vặn trong l&ograve;ng b&agrave;n tay.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/229948/oppo-a15s-253621-103657.jpg\"><img class=\"lazy\" title=\"Điện thoại OPPO A15s | Thiết kế cạnh viền\" src=\"https://cdn.tgdd.vn/Products/Images/42/229948/oppo-a15s-253621-103657.jpg\" alt=\"Điện thoại OPPO A15s | Thiết kế cạnh viền\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/229948/oppo-a15s-253621-103657.jpg\" /></a></p>\r\n<p dir=\"ltr\">Nhược điểm của mặt lưng n&agrave;y l&agrave; v&igrave; l&agrave;m từ chất liệu nhựa b&oacute;ng n&ecirc;n kh&aacute; dễ bị b&aacute;m mồ h&ocirc;i v&agrave; dấu v&acirc;n tay. Tuy nhi&ecirc;n, ch&uacute;ng ta kh&ocirc;ng cần lo lắng về điều n&agrave;y cho lắm v&igrave; nh&agrave; sản xuất đ&atilde; ưu &aacute;i tặng k&egrave;m một chiếc ốp lưng sẵn trong hộp.</p>', 3990000, 'uploads/products/oppo-a15s-(2).jpg', 34, '1', '2021-10-18 03:04:40', '2021-10-18 03:04:40', 1, NULL),
(22, 'iPhone Xiaomi Redmi Note 10 (6GB/128GB)', '<h3><a href=\"https://www.thegioididong.com/dtdd-xiaomi\">Xiaomi</a>&nbsp;đ&atilde; tr&igrave;nh l&agrave;ng chiếc điện thoại mang t&ecirc;n gọi l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-10\">Xiaomi Redmi Note 10</a>&nbsp;với điểm nhấn ch&iacute;nh l&agrave; cụm 4 camera 48 MP, chip rồng Snapdragon 678 mạnh mẽ c&ugrave;ng nhiều n&acirc;ng cấp như dung lượng pin 5.000 mAh v&agrave; hỗ trợ sạc nhanh 33 W tiện lợi.</h3>', '<h3 dir=\"ltr\">Thiết kế đẹp mắt, sang trọng đến từng chi tiết</h3>\r\n<p dir=\"ltr\">Xiaomi Redmi Note 10 sở hữu khung viền kim loại kết hợp với mặt lưng nhựa c&oacute; hiệu ứng m&agrave;u sắc đem lại một ngoại h&igrave;nh sang trọng v&agrave; bắt mắt.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-002.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Thiết kế đẹp mắt, sang trọng đến từng chi tiết\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-002.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Thiết kế đẹp mắt, sang trọng đến từng chi tiết\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-002.jpg\" /></a></p>\r\n<p dir=\"ltr\">Tổng trọng lượng của m&aacute;y chỉ 178.8 g c&ugrave;ng thiết kế&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại smartphone thiết kế mỏng nhẹ\" href=\"https://www.thegioididong.com/dtdd-mong-nhe\" target=\"_blank\" rel=\"noopener\">mỏng nhẹ</a>&nbsp;ấn tượng với độ mỏng 8.29 mm, cho trải nghiệm cầm nắm thao t&aacute;c thoải m&aacute;i, mang điện thoại đi khắp nơi thật dễ d&agrave;ng.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-004.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Thiết kế cạnh viền\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-004.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Thiết kế cạnh viền\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-004.jpg\" /></a></p>\r\n<p dir=\"ltr\"><a title=\"Tham khảo gi&aacute; điện thoại smartphone c&oacute; bảo mật cảm biến v&acirc;n tay\" href=\"https://www.thegioididong.com/dtdd-bao-mat-van-tay\" target=\"_blank\" rel=\"noopener\">Cảm biến v&acirc;n tay&nbsp;</a>một chạm được t&iacute;ch hợp ở cạnh viền gi&uacute;p việc mở kh&oacute;a điện thoại nhanh ch&oacute;ng v&agrave; cho cảm gi&aacute;c cầm tay thoải m&aacute;i.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-010.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Cảm biến v&acirc;n tay ở cạnh viền\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-010.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Cảm biến v&acirc;n tay ở cạnh viền\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-010.jpg\" /></a></p>\r\n<p dir=\"ltr\">Xiaomi Redmi Note 10 đem đến cho người ti&ecirc;u d&ugrave;ng 3 t&ugrave;y chọn m&agrave;u sắc bao gồm: Trắng, Đen v&agrave; Xanh tự do lựa chọn cho phong c&aacute;ch của bạn.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-8.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Nhiều m&agrave;u sắc, thoải m&aacute;i lựa chọn\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-8.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Nhiều m&agrave;u sắc, thoải m&aacute;i lựa chọn\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-8.jpg\" /></a></p>\r\n<h3 dir=\"ltr\">M&agrave;n h&igrave;nh si&ecirc;u lớn, trải nghiệm ho&agrave;n hảo</h3>\r\n<p dir=\"ltr\">Màn hình của Redmi Note 10 được trang bị m&agrave;n h&igrave;nh giải tr&iacute; k&iacute;ch thước 6.43 inch, đ&ocirc;̣ ph&acirc;n giải Full HD+, mật độ điểm ảnh cao đến tận 409 ppi, đem đến khả năng hiển thị v&ocirc; c&ugrave;ng sắc n&eacute;t.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-001.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | M&agrave;n h&igrave;nh si&ecirc;u lớn, trải nghiệm ho&agrave;n hảo\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-001.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | M&agrave;n h&igrave;nh si&ecirc;u lớn, trải nghiệm ho&agrave;n hảo\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-001.jpg\" /></a></p>\r\n<p dir=\"ltr\">B&ecirc;n cạnh đ&oacute;, tấm nền AMOLED tr&ecirc;n Redmi Note 10 c&ograve;n cho độ s&aacute;ng cao, m&agrave;u sắc trung thực, đem đến cho người d&ugrave;ng trải nghiệm giải tr&iacute; đỉnh cao.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-16.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Chất lượng hiển thị sắc n&eacute;t\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-16.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Chất lượng hiển thị sắc n&eacute;t\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-16.jpg\" /></a></p>\r\n<p dir=\"ltr\">Nổi bật hơn cả ch&iacute;nh l&agrave; tr&ecirc;n Xiaomi Redmi Note 10 sử dụng m&agrave;n h&igrave;nh đục lỗ, với viền m&agrave;n h&igrave;nh được thiết kế mỏng gi&uacute;p tối ưu h&oacute;a diện t&iacute;ch hiển thị của m&agrave;n h&igrave;nh mang đến trải nghiệm kh&ocirc;ng gian rộng r&atilde;i đủ để bạn thoải m&aacute;i sử dụng.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-008.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | M&agrave;n h&igrave;nh đục lỗ tr&agrave;n viền\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-008.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | M&agrave;n h&igrave;nh đục lỗ tr&agrave;n viền\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-008.jpg\" /></a></p>\r\n<p dir=\"ltr\">Đồng thời, m&agrave;n h&igrave;nh tr&ecirc;n Redmi Note 10 c&ograve;n được chứng nhận bởi SGS, giảm lượng &aacute;nh s&aacute;ng xanh c&oacute; hại cho mắt, cho bạn thoải m&aacute;i sử dụng giải tr&iacute;, đọc b&aacute;o, xem phim trong thời gian d&agrave;i.&nbsp;</p>\r\n<p dir=\"ltr\">Ngo&agrave;i ra, Redmi Note 10 c&ograve;n hỗ trợ hệ thống &acirc;m thanh Hi-res Audio với &acirc;m lượng cao v&agrave; dải &acirc;m rộng, cho bạn đắm ch&igrave;m trong kh&ocirc;ng gian giải tr&iacute; bất tận.</p>\r\n<h3 dir=\"ltr\">Hiệu năng mượt m&agrave; với chip rồng Snapdragon</h3>\r\n<p dir=\"ltr\">Xiaomi Redmi Note 10 được trang bị bộ vi xử l&yacute; Snapdragon 678 được sản xuất tr&ecirc;n tiến tr&igrave;nh 11 nm hiện đại đem đến một hiệu năng mạnh mẽ, c&ugrave;ng khả năng xử l&yacute; mượt m&agrave; c&aacute;c t&aacute;c vụ nhẹ nh&agrave;ng như lướt Facebook, xem phim hay thậm ch&iacute; l&agrave; chơi c&aacute;c tựa game nặng.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-013.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Hiệu năng mạnh mẽ với chip rồng Snapdragon\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-013.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Hiệu năng mạnh mẽ với chip rồng Snapdragon\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-013.jpg\" /></a></p>\r\n<p dir=\"ltr\">Đi k&egrave;m với đ&oacute; l&agrave; dung lượng&nbsp;<a title=\"Tham khảo điện thoại c&oacute; dung lượng RAM dưới từ 4 đến 6 GB đang b&aacute;n tại TGDĐ\" href=\"https://www.thegioididong.com/dtdd-ram-4-den-6gb\" target=\"_blank\" rel=\"noopener\">RAM 6 GB</a>&nbsp;cho khả năng đa nhiệm tuyệt vời, mở nhiều ứng dụng c&ugrave;ng một l&uacute;c ho&agrave;n to&agrave;n kh&ocirc;ng bị giật lag.</p>\r\n<p dir=\"ltr\"><a title=\"Tham khảo điện thoại c&oacute; bộ nhớ trong từ 128 đến 256 GB đang b&aacute;n tại TGDĐ\" href=\"https://www.thegioididong.com/dtdd-rom-128-den-256gb\" target=\"_blank\" rel=\"noopener\">Bộ nhớ trong 128 GB</a>&nbsp;c&oacute; thể mở rộng khả năng lưu trữ đến 512 GB qua thẻ nhớ ngo&agrave;i Micro SD, cho bạn thoải m&aacute;i sử dụng dữ liệu, c&agrave;i đặt được nhiều ứng dụng hay c&aacute;c tựa game nặng.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-007.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Hỗ trợ khe cắm thẻ nhớ ngo&agrave;i tới 512 GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-007.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Hỗ trợ khe cắm thẻ nhớ ngo&agrave;i tới 512 GB\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-007.jpg\" /></a></p>\r\n<p dir=\"ltr\">Hơn thế nữa, Redmi Note 10 c&ograve;n được c&agrave;i đặt sẵn giao diện MIUI 12 mới, t&ugrave;y biến tr&ecirc;n nền Android 11 với nhiều n&acirc;ng cấp về giao diện lẫn hiệu năng, để mang tới một trải nghiệm giải tr&iacute; tốt nhất.</p>\r\n<h3 dir=\"ltr\">Bộ bốn camera si&ecirc;u n&eacute;t, bắt trọn mọi khoảnh khắc</h3>\r\n<p dir=\"ltr\">Xiaomi Redmi Note được trang bị cụm camera sau với độ ph&acirc;n giải lần lượt l&agrave; 48 MP hỗ trợ chụp ảnh g&oacute;c rộng, camera chụp ảnh&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại smartphone camera chụp h&igrave;nh g&oacute;c rộng\" href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_top\">g&oacute;c si&ecirc;u rộng</a>&nbsp;8 MP, hai camera 2 MP đo chiều s&acirc;u ảnh v&agrave; chụp ảnh&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại smartphone camera macro\" href=\"https://www.thegioididong.com/dtdd-camera-macro\" target=\"_blank\" rel=\"noopener\">macro</a>.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-003.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Cụm 4 camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-003.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Cụm 4 camera sau\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-003.jpg\" /></a></p>\r\n<p dir=\"ltr\">Cụm camera tr&ecirc;n Redmi Note 10 được hỗ trợ bằng c&ocirc;ng nghệ AI th&ocirc;ng minh, cho bạn ghi lại mọi khoảnh khắc trong cuộc sống h&agrave;ng ng&agrave;y của bạn một c&aacute;ch dễ d&agrave;ng v&agrave; đầy chi tiết.&nbsp;</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-13.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Ảnh chụp bằng camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-13.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Ảnh chụp bằng camera sau\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-13.jpg\" /></a></p>\r\n<p dir=\"ltr\">Với camera g&oacute;c si&ecirc;u rộng 8 MP, cho độ mở l&ecirc;n tới 118&deg; gi&uacute;p bạn c&oacute; thể ghi lại những d&atilde;y n&uacute;i rộng lớn, những t&ograve;a nh&agrave; chọc trời hay những cột mốc ở xa một c&aacute;ch dễ d&agrave;ng m&agrave; kh&ocirc;ng cần phải di chuyển nhiều.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-10.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Ảnh chụp g&oacute;c rộng\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-10.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Ảnh chụp g&oacute;c rộng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-10.jpg\" /></a></p>\r\n<p dir=\"ltr\">Ngo&agrave;i ra, camera macro v&agrave; camera đo chiều s&acirc;u l&agrave; c&ocirc;ng cụ đắc lực để bạn c&oacute; được những bức ảnh độc đ&aacute;o ở khoảng c&aacute;ch gần hay chụp ảnh&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại smartphone camera chụp h&igrave;nh xo&aacute; ph&ocirc;ng\" href=\"https://www.thegioididong.com/dtdd-camera-xoa-phong\" target=\"_blank\" rel=\"noopener\">x&oacute;a ph&ocirc;ng</a>&nbsp;một c&aacute;ch ảo diệu m&agrave; kh&ocirc;ng cần phải sử dụng đến ứng dụng thứ ba.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-5.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Ảnh chụp bằng camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-5.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Ảnh chụp bằng camera sau\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-5.jpg\" /></a></p>\r\n<p dir=\"ltr\">Camera trước của m&aacute;y được thiết kế dạng đục lỗ c&oacute; độ ph&acirc;n giải 13 MP hỗ trợ đầy đủ các tính năng l&agrave;m đẹp AI Beautify, chụp ảnh g&oacute;c rộng, x&oacute;a ph&ocirc;ng với nhiều hiệu ứng bokeh độc đ&aacute;o cho bạn sẵn s&agrave;ng chia sẻ ảnh cho người th&acirc;n v&agrave; gia đ&igrave;nh l&ecirc;n mạng x&atilde; hội bất cứ l&uacute;c n&agrave;o.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-12.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Ảnh chụp selfie\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-12.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Ảnh chụp selfie\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-12.jpg\" /></a></p>\r\n<h3 dir=\"ltr\">Thoải m&aacute;i sử dụng với dung lượng pin 5.000 mAh, tích hợp sạc nhanh 33 W&nbsp;</h3>\r\n<p dir=\"ltr\">Xiaomi trang bị cho Xiaomi Redmi Note 10 vi&ecirc;n pin khủng 5.000 mAh, cho ph&eacute;p bạn sử dụng thoải m&aacute;i trong 1.5 ng&agrave;y chỉ sau duy nhất một lần sạc.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-006.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Dung lượng pin lớn, thoải m&aacute;i trải nghiệm\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-006.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Dung lượng pin lớn, thoải m&aacute;i trải nghiệm\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-006.jpg\" /></a></p>\r\n<p dir=\"ltr\">Ngo&agrave;i ra, Xiaomi Redmi Note 10 c&oacute; khả năng<a title=\"Tham khảo gi&aacute; điện thoại smartphone sạc pin nhanh\" href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" rel=\"noopener\">&nbsp;sạc nhanh&nbsp;</a>33 W, nh&agrave; sản xuất tự tin cho biết rằng n&oacute; cho khả năng sạc đầy 50% pin chỉ trong khoảng 30 ph&uacute;t, cho ph&eacute;p bạn tận dụng tối đa thời gian sử dụng điện thoại của m&igrave;nh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/222758/xiaomi-redmi-note-10-9.jpg\"><img class=\"lazy\" title=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Khả năng sạc nhanh 33W\" src=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-9.jpg\" alt=\"Điện thoại Xiaomi Redmi Note 10 (6GB/128GB) | Khả năng sạc nhanh 33W\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/222758/xiaomi-redmi-note-10-9.jpg\" /></a></p>\r\n<p dir=\"ltr\">Nh&igrave;n chung, Xiaomi Redmi Note 10 l&agrave; chiếc smartphone ho&agrave;n hảo, đ&aacute;p ứng tốt cho c&aacute;c nhu cầu giải tr&iacute;, chụp ảnh hay chơi game. Đặc biệt với mức gi&aacute; b&aacute;n v&ocirc; c&ugrave;ng hấp dẫn, chắc chắn đ&acirc;y sẽ l&agrave; một t&acirc;n binh s&aacute;ng gi&aacute; tr&ecirc;n thị trường smartphone đang rất s&ocirc;i động hiện nay.</p>', 5190000, 'uploads/products/xiaomi-redmi-note-10-thumb-green-600x600-1-600x600.jpg', 45, '1', '2021-10-18 03:06:48', '2021-10-18 03:12:03', 1, NULL),
(23, 'iPhone Samsung Galaxy Z Flip', '<h2 dir=\"ltr\">Cuối c&ugrave;ng sau bao nhi&ecirc;u thời gian chờ đợi, chiếc điện thoại&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại Samsung Galaxy Z Flip ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-flip\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; điện thoại Samsung Galaxy Z Flip ch&iacute;nh h&atilde;ng\">Samsung Galaxy Z Flip</a>&nbsp;đ&atilde; được&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại smartphone Samsung ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; điện thoại smartphone Samsung ch&iacute;nh h&atilde;ng\">Samsung</a>&nbsp;ra mắt tại sự kiện&nbsp;Unpacked 2020. Si&ecirc;u phẩm với thiết kế m&agrave;n h&igrave;nh gập vỏ s&ograve; độc đ&aacute;o, hiệu năng tuyệt đỉnh c&ugrave;ng nhiều c&ocirc;ng nghệ thời thượng, dẫn dầu 2020.</h2>', '<h3 dir=\"ltr\">Đột ph&aacute; với thiết kế m&agrave;n h&igrave;nh gập</h3>\r\n<p dir=\"ltr\">Samsung Galaxy Z Flip được thiết kế với kiểu d&aacute;ng m&agrave;n h&igrave;nh gập lấy &yacute; tưởng từ d&ograve;ng sản phẩm&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại Samsung Galaxy Fold ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-fold\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; điện thoại Samsung Galaxy Fold ch&iacute;nh h&atilde;ng\">Galaxy Fold</a>&nbsp;từng g&acirc;y nhiều tiếng vang trong năm 2019.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/213022/samsung-galaxy-z-flip-tgdd4-1.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Flip | Khả năng gập với nhiều g&oacute;c độ kh&aacute;c nhau\" src=\"https://cdn.tgdd.vn/Products/Images/42/213022/samsung-galaxy-z-flip-tgdd4-1.jpg\" alt=\"Điện thoại Samsung Galaxy Z Flip | Khả năng gập với nhiều g&oacute;c độ kh&aacute;c nhau\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/213022/samsung-galaxy-z-flip-tgdd4-1.jpg\" /></a></p>\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n điểm kh&aacute;c biệt l&agrave; m&agrave;n h&igrave;nh của Z Flip được&nbsp;thiết kế gập theo chiều dọc, khiến cho tổng thể m&aacute;y c&oacute; thể nằm gọn trong l&ograve;ng b&agrave;n tay của người d&ugrave;ng như một phụ kiện thời trang cao cấp.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/213022/samsung-galaxy-z-flip-tgdd2-1.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Flip | Đột ph&aacute; thiết kế m&agrave;n h&igrave;nh gập\" src=\"https://cdn.tgdd.vn/Products/Images/42/213022/samsung-galaxy-z-flip-tgdd2-1.jpg\" alt=\"Điện thoại Samsung Galaxy Z Flip | Đột ph&aacute; thiết kế m&agrave;n h&igrave;nh gập\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/213022/samsung-galaxy-z-flip-tgdd2-1.jpg\" /></a></p>\r\n<p dir=\"ltr\">Bản lề của m&aacute;y cũng c&oacute; khả năng gập - mở với nhiều g&oacute;c độ kh&aacute;c nhau, khi thiết bị ở trạng th&aacute;i mở một nửa, m&agrave;n h&igrave;nh sẽ tự động chia th&agrave;nh hai m&agrave;n h&igrave;nh 4 inch vừa đủ để bạn c&oacute; thể dễ d&agrave;ng xem h&igrave;nh ảnh, nội dung hoặc video ở nửa tr&ecirc;n của m&agrave;n h&igrave;nh v&agrave; thao t&aacute;c điều khiển ch&uacute;ng ở nửa dưới.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/213022/samsung-galaxy-z-flip-tgdd5-1.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Flip | Thiết kế bản lề mới với độ bền cao\" src=\"https://cdn.tgdd.vn/Products/Images/42/213022/samsung-galaxy-z-flip-tgdd5-1.jpg\" alt=\"Điện thoại Samsung Galaxy Z Flip | Thiết kế bản lề mới với độ bền cao\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/213022/samsung-galaxy-z-flip-tgdd5-1.jpg\" /></a></p>\r\n<p dir=\"ltr\">Trải nghiệm sử dụng Samsung Galaxy Z Flip linh hoạt nhờ kết cấu bản lề mới hiện đại, c&oacute; khả năng chống bụi bẩn tốt hơn. Tuy vẫn c&oacute; vết gấp giữa m&agrave;n h&igrave;nh Galaxy Z Flip nhưng ho&agrave;n to&agrave;n kh&ocirc;ng ảnh hưởng đến trải nghiệm người d&ugrave;ng.</p>\r\n<p dir=\"ltr\">B&ecirc;n cạnh đ&oacute; c&ocirc;ng nghệ n&agrave;y c&ograve;n cho ph&eacute;p Samsung Galaxy Z Flip c&oacute; thể dễ d&agrave;ng gập mở với độ bền l&ecirc;n tới hơn 200.000 lần, mở ra một thập kỷ mới của sự s&aacute;ng tạo d&agrave;nh cho&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại smartphone ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; điện thoại smartphone ch&iacute;nh h&atilde;ng\">điện thoại</a>&nbsp;m&agrave;n h&igrave;nh gập.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/213022/samsung-galaxy-z-flip-tgdd11-2.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Flip | Thiết kế gọn nhẹ\" src=\"https://cdn.tgdd.vn/Products/Images/42/213022/samsung-galaxy-z-flip-tgdd11-2.jpg\" alt=\"Điện thoại Samsung Galaxy Z Flip | Thiết kế gọn nhẹ\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/213022/samsung-galaxy-z-flip-tgdd11-2.jpg\" /></a></p>\r\n<p dir=\"ltr\">Khi mở m&aacute;y hết cỡ, m&agrave;n h&igrave;nh của m&aacute;y c&oacute; k&iacute;ch thước 6.7 inch, đ&acirc;y l&agrave; m&agrave;n h&igrave;nh c&oacute; thiết kế gập vỏ s&ograve; bằng k&iacute;nh đầu ti&ecirc;n tr&ecirc;n thế giới với thiết kế&nbsp;<a title=\"Đi&ecirc;n thoại Tr&agrave;n viền tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-man-hinh-tran-vien\" target=\"_blank\" rel=\"noopener\" type=\"Đi&ecirc;n thoại Tr&agrave;n viền tại Thegioididong.com\">m&agrave;n h&igrave;nh tr&agrave;n viền</a>&nbsp;với camera kho&eacute;t lỗ.&nbsp;</p>\r\n<p dir=\"ltr\">Galaxy Z Flip sở hữu m&agrave;n h&igrave;nh Infinity Flex với c&ocirc;ng nghệ k&iacute;nh uốn dẻo Ultra Thin Glass (UTG) độc đ&aacute;o từ Samsung, c&ocirc;ng nghệ n&agrave;y gi&uacute;p m&aacute;y mỏng hơn, cho cảm gi&aacute;c cầm nắm sang trọng v&agrave; cao cấp.</p>\r\n<p dir=\"ltr\"><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/213022/samsung-galaxy-z-flip-tgdd10-2.jpg\"><img class=\"lazy\" title=\"Điện thoại Samsung Galaxy Z Flip | M&agrave;n h&igrave;nh giải tr&iacute; sắc n&eacute;t\" src=\"https://cdn.tgdd.vn/Products/Images/42/213022/samsung-galaxy-z-flip-tgdd10-2.jpg\" alt=\"Điện thoại Samsung Galaxy Z Flip | M&agrave;n h&igrave;nh giải tr&iacute; sắc n&eacute;t\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/213022/samsung-galaxy-z-flip-tgdd10-2.jpg\" /></a></p>\r\n<p dir=\"ltr\">Sử dụng tấm nền Dynamic AMOLED độ ph&acirc;n giải Full HD+ tỉ lệ m&agrave;n h&igrave;nh 21.9:9 độc đ&aacute;o nhất hiện nay, hỗ trợ HDR10+ gi&uacute;p cho từng h&igrave;nh ảnh m&agrave; bạn xem sống động tr&ecirc;n từng chi tiết, sắc n&eacute;t tr&ecirc;n từng chuyển động mang đến m&agrave;u sắc sống động ch&acirc;n thật.</p>', 36000000, 'uploads/products/samsung-galaxy-z-flip-600x600-1-600x600.jpg', 24, '1', '2021-10-18 03:08:06', '2021-10-18 03:08:06', 1, NULL);
INSERT INTO `products` (`id`, `name`, `desc`, `content`, `price`, `thumbnail`, `qty`, `status`, `created_at`, `updated_at`, `cat_id`, `deleted_at`) VALUES
(24, 'Laptop Apple MacBook Air 2017 i5 1.8GHz/8GB/128GB', '<h3>Chiếc đi&ecirc;̣n thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-a15s\">OPPO A15s</a>&nbsp;là m&ocirc;̣t phi&ecirc;n bản n&acirc;ng cấp của&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-a15\">OPPO A15</a>, có c&acirc;́u hình &acirc;́n tượng trong t&acirc;̀m giá đủ đ&ecirc;̉ người dùng thỏa sức làm vi&ecirc;̣c v&agrave; giải trí khi c&acirc;̀n thi&ecirc;́t.</h3>', '<h3>Hiệu năng vượt xa mọi giới hạn</h3>\r\n<p>Apple đ&atilde; trang bị con chip mới nhất của h&atilde;ng (t&iacute;nh đến 11/2020) cho iPhone 12 đ&oacute; l&agrave;&nbsp;<a title=\"T&igrave;m hiểu chip A14 Bionic l&agrave; g&igrave;?\" href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-a14-bionic-tren-iphone-12-va-ipad-1290695\" target=\"_blank\" rel=\"noopener\">A14 Bionic</a>, được sản xuất tr&ecirc;n tiến tr&igrave;nh 5 nm với hiệu suất ổn định hơn so với chip A13 được trang bị tr&ecirc;n phi&ecirc;n bản tiền nhiệm&nbsp;<a title=\"Tham khảo điện thoại iPhone 11 ch&iacute;nh h&atilde;ng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/iphone-11\" target=\"_blank\" rel=\"noopener\">iPhone 11</a>.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/213031/iphone-12-144220-044259.jpg\"><img class=\"lazy\" title=\"Chip A14 Bionic mạnh mẽ | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-144220-044259.jpg\" alt=\"Chip A14 Bionic mạnh mẽ | iPhone 12\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-144220-044259.jpg\" /></a></p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"T&igrave;m hiểu về chip Apple A14 Bionic tr&ecirc;n iPhone 12 v&agrave; iPad Air 2020\" href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-a14-bionic-tren-iphone-12-va-ipad-1290695\" target=\"_blank\" rel=\"noopener\">T&igrave;m hiểu về chip Apple A14 Bionic tr&ecirc;n iPhone 12 v&agrave; iPad Air 2020</a></p>\r\n<p>Với CPU Apple A14 Bionic, bạn c&oacute; thể dễ d&agrave;ng trải nghiệm mọi tựa game với những pha chuyển cảnh mượt m&agrave; hay h&agrave;ng loạt hiệu ứng đồ họa tuyệt đẹp ở mức đồ họa cao m&agrave; kh&ocirc;ng lo t&igrave;nh trạng giật lag.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/42/213031/iphone-12-18.jpg\"><img class=\"lazy\" title=\"Chiến game si&ecirc;u mượt, đồ họa tuyệt đẹp | iPhone 12\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-18.jpg\" alt=\"Chiến game si&ecirc;u mượt, đồ họa tuyệt đẹp | iPhone 12\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-18.jpg\" /></a></p>\r\n<p>Chưa hết, Apple c&ograve;n g&acirc;y bất ngờ đến người d&ugrave;ng với hệ thống 5G lần đầu ti&ecirc;n được trang bị tr&ecirc;n những chiếc&nbsp;<a title=\"Tham khảo iPhone kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" rel=\"noopener\">iPhone</a>, cho tốc độ truyền tải dữ liệu nhanh hơn, ổn định hơn.</p>', 8990000, 'uploads/products/hp-15s-fq1111tu-i3-193r0pa-224012-224012-600x600.jpg', 35, '1', '2021-10-18 03:10:29', '2021-10-18 03:10:29', 2, NULL),
(25, 'Laptop Asus VivoBook X509JA i3 1005G1/4GB/256GB/Win9', '<h2><a title=\"Laptop Asus VivoBook A515EA i3 1115G4/8GB/512GB/Win10 (BQ497T)\" href=\"https://www.thegioididong.com/laptop/asus-vivobook-a515ea-i3-bq497t\" target=\"_blank\" rel=\"noopener\" type=\"Laptop Asus VivoBook A515EA i3 1115G4/8GB/512GB/Win10 (BQ497T)\">Laptop Asus VivoBook i3 A515EA (BQ497T)</a>&nbsp;hiện đại với mức gi&aacute; tốt, được trang bị chip xử l&yacute; Intel thế hệ 11 mới nhất c&ugrave;ng bộ nhớ khủng, rất đ&aacute;ng để giới học sinh sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng sở hữu.</h2>', '<h3>Thiết kế mỏng nhẹ, năng động</h3>\r\n<p>Laptop Asus VivoBook i3 A515EA (BQ497T) c&oacute; kiểu d&aacute;ng trẻ trung, m&agrave;u sắc sang trọng đẹp mắt với gam m&agrave;u bạc thời trang.&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c mẫu laptop đang kinh doanh tại thegioididong.com\">Laptop</a>&nbsp;mỏng&nbsp;<strong>17.9 mm</strong>&nbsp;v&agrave; c&oacute; khối lượng&nbsp;<strong>1.8 kg</strong>&nbsp;dễ d&agrave;ng để c&aacute;c bạn cất gọn v&agrave;o ba l&ocirc; mang theo đi học hằng ng&agrave;y.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043444.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043444.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) Thiết kế\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043444.jpg\" /></a></p>\r\n<p>Thiết kế b&agrave;n ph&iacute;m fullsize với h&agrave;nh tr&igrave;nh ph&iacute;m&nbsp;<strong>1.4 mm</strong>&nbsp;cảm gi&aacute;c nhập liệu l&acirc;u kh&ocirc;ng bị mỏi. Hơi đ&aacute;ng tiếc khi b&agrave;n ph&iacute;m của d&ograve;ng VivoBook n&agrave;y kh&ocirc;ng được trang bị đ&egrave;n nền, nếu kh&ocirc;ng th&igrave; độ ho&agrave;n hảo sẽ c&agrave;ng tăng cao.&nbsp;</p>\r\n<p>Cảm biến v&acirc;n tay nhạy b&eacute;n được t&iacute;ch hợp gọn g&agrave;ng tr&ecirc;n touchpad gi&uacute;p bạn đăng nhập nhanh ch&oacute;ng v&agrave; an to&agrave;n.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043449.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cảm biến v&acirc;n tay\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043449.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cảm biến v&acirc;n tay\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043449.jpg\" /></a></p>\r\n<p>Được trang bị c&aacute;c cổng bao gồm USB 3.2, USB 2.0, HDMI, USB Type-C với tốc độ truyền tải dữ liệu ổn định,&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm Asus VivoBook đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus-vivobook\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm Asus VivoBook đang b&aacute;n tại Thegioididong.com\">Asus VivoBook</a>&nbsp;A515EA dễ d&agrave;ng kết nối m&aacute;y chiếu, tivi hay c&aacute;c thiết bị ngoại vi hỗ trợ kh&aacute;c.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043454.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cổng kết nối\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043454.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cổng kết nối\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093420-043454.jpg\" /></a></p>\r\n<h3>Lướt web, xem phim mượt m&agrave;</h3>\r\n<p>Asus VivoBook A515EA được trang bị chip Intel&nbsp;<strong><a title=\"Xem th&ecirc;m c&aacute;c laptop Core i3 đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=core-i3\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c laptop Core i3 đang b&aacute;n tại Thegioididong.com\">Core i3</a></strong>&nbsp;thuộc&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c laptop Intel Gen 11 đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-cpu-intel-gen-11\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c laptop Intel Gen 11 đang b&aacute;n tại Thegioididong.com\">thế hệ 11</a>&nbsp;mới nhất với bộ xử l&yacute; m&atilde; 1115G4 cho hiệu năng đủ d&ugrave;ng, thao t&aacute;c mượt m&agrave; với c&aacute;c ứng dụng như Office 365, tr&igrave;nh duyệt Google Chrome, Microsoft Edge, tốc độ tải dữ liệu ổn định.</p>\r\n<p><a title=\"Laptop được trang bị RAM 8 GB đang kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=8-gb\" target=\"_blank\" rel=\"noopener\" type=\"Laptop được trang bị RAM 8 GB đang kinh doanh tại Thegioididong.com\"><strong>RAM 8 GB</strong></a>&nbsp;cho khả năng đa nhiệm tốt, bạn c&oacute; thể vừa chạy deadline, vừa nghe một bản nhạc kh&ocirc;ng lời để giải toả căng thẳng m&agrave; kh&ocirc;ng l&agrave;m ảnh hưởng đến mạch l&agrave;m việc.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043513.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cấu h&igrave;nh\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043513.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Cấu h&igrave;nh\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043513.jpg\" /></a></p>\r\n<p>Hơn nữa, m&aacute;y c&ograve;n c&oacute;&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus c&oacute; SSD tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus-o-cung-ssd\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus c&oacute; SSD tại Thegioididong.com\">ổ cứng SSD</a>&nbsp;512 GB cho tốc độ khởi động laptop hay mở c&aacute;c ứng dụng đều chỉ trong v&ograve;ng v&agrave;i gi&acirc;y. V&agrave; với dung lượng&nbsp;<strong><a title=\"Xem th&ecirc;m c&aacute;c laptop c&oacute; SSD 512 GB\" href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c laptop c&oacute; SSD 512 GB\">512 GB</a></strong>&nbsp;th&igrave; việc lưu trữ h&igrave;nh ảnh, t&agrave;i liệu học tập,&hellip; cũng thoải m&aacute;i hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043503.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - SSD\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043503.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - SSD\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043503.jpg\" /></a></p>\r\n<p>Chưa dừng lại ở đ&oacute;, VivoBook A515EA c&ograve;n c&oacute; t&iacute;nh năng Link to My ASUS, thiết bị di động của bạn sẽ được kết nối v&agrave; hiển thị tr&ecirc;n m&aacute;y t&iacute;nh Asus, bạn dễ d&agrave;ng thao t&aacute;c c&aacute;c chức năng của thiết bị di động th&ocirc;ng qua chuột m&aacute;y t&iacute;nh, b&agrave;n ph&iacute;m của VivoBook A515EA.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043518.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Phần mềm myAsus\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043518.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - Phần mềm myAsus\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043518.jpg\" /></a></p>\r\n<h3>M&agrave;n h&igrave;nh tr&agrave;n viền nh&igrave;n cực đ&atilde; mắt</h3>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus đang b&aacute;n tại Thegioididong.com\">Laptop Asus</a>&nbsp;VivoBook A515EA sở hữu m&agrave;n h&igrave;nh&nbsp;<a title=\"Xem th&ecirc;m một số laptop m&agrave;n h&igrave;nh tr&ecirc;n 15 inch đang được kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-tren-15-inch\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m một số laptop m&agrave;n h&igrave;nh tr&ecirc;n 15 inch đang được kinh doanh tại Thegioididong.com\"><strong>15.6 inch</strong></a>&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus c&oacute; Viền m&agrave;n h&igrave;nh mỏng tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus-vien-man-hinh-mong\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop Asus c&oacute; Viền m&agrave;n h&igrave;nh mỏng tại Thegioididong.com\">viền mỏng</a>&nbsp;c&oacute; độ ph&acirc;n giải Full HD cho ra những thước phim m&agrave;u sắc sống động h&uacute;t mắt. Tấm nền IPS cho g&oacute;c nh&igrave;n rộng gi&uacute;p tạo cảm gi&aacute;c kh&ocirc;ng gian hiển thị rộng r&atilde;i v&agrave; thoải m&aacute;i hơn kết hợp c&ugrave;ng c&ocirc;ng nghệ chống ch&oacute;i Anti Glare, l&agrave;m việc nơi g&oacute;c cửa sổ &aacute;nh s&aacute;ng chiếu ch&iacute;nh diện v&agrave;o m&agrave;n h&igrave;nh cũng kh&ocirc;ng l&agrave;m ảnh hưởng năng suất l&agrave;m việc của bạn.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043508.jpg\"><img class=\"lazy\" title=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - M&agrave;n h&igrave;nh\" src=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043508.jpg\" alt=\"Laptop Asus VivoBook A515EA i3 (BQ497T) - M&agrave;n h&igrave;nh\" data-original=\"https://cdn.tgdd.vn/Products/Images/44/230429/asus-vivobook-a515ea-i3-bq497t-093520-043508.jpg\" /></a></p>\r\n<p>Hiện đại v&agrave; tiện dụng l&agrave; những cụm từ d&ugrave;ng để m&ocirc; tả chiếc&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop học tập, văn ph&ograve;ng đang b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" rel=\"noopener\" type=\"Xem th&ecirc;m c&aacute;c sản phẩm laptop học tập, văn ph&ograve;ng đang b&aacute;n tại Thegioididong.com\">laptop học tập văn ph&ograve;ng</a>&nbsp;Asus VivoBook A515EA (BQ497T). Với thiết kế mỏng nhẹ c&ugrave;ng dung lượng khủng của ổ cứng SSD, chiếc laptop n&agrave;y sẽ đem đến những trải nghiệm tuyệt vời cho người d&ugrave;ng, đặc biệt l&agrave; với giới trẻ.&nbsp;</p>', 14090000, 'uploads/products/asus-vivobook-x509ja-i3-ej480t-225608-200x200.jpg', 34, '1', '2021-10-18 12:54:56', '2021-10-18 12:54:56', 2, NULL),
(26, 'iPad Pro 12.9 inch Wifi 128GB (2020)', '<p>Sở hữu thiết kế tinh tế, m&agrave;n h&igrave;nh xuất sắc v&agrave; cấu h&igrave;nh mạnh mẽ, đ&aacute;p ứng được hầu hết nhu cầu của một người s&aacute;ng tạo chuy&ecirc;n nghiệp. Điều kh&ocirc;ng một thế hệ <a title=\"M&aacute;y t&iacute;nh bản iPad Pro 12.9 2020\" href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\" type=\"M&aacute;y t&iacute;nh bản iPad Pro 12.9 2020\">m&aacute;y t&iacute;nh bảng</a>&nbsp;n&agrave;o kh&aacute;c c&oacute; thể l&agrave;m được, đ&atilde; xuất hiện tr&ecirc;n&nbsp;<a title=\"iPad Pro 12.9 2020\" href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-12-9-inch-wifi-128gb-2020\" target=\"_blank\" rel=\"noopener\" type=\"Sản phẩm iPad Pro 12.9 2020\">iPad Pro 12.9 inch 2020</a>.</p>', '<h3>M&agrave;n h&igrave;nh tuyệt đẹp, nhiều c&ocirc;ng nghệ t&iacute;ch hợp</h3>\r\n<p>iPad Pro 12.9 inch 2020 c&oacute; một thiết kế kh&aacute; vu&ocirc;ng vức với viền m&agrave;n h&igrave;nh 4 cạnh kh&ocirc;ng qu&aacute; d&agrave;y v&agrave; đều nhau, gi&uacute;p cho trải nghiệm cầm nắm dễ d&agrave;ng, gi&uacute;p cho tổng thể mặt trước của&nbsp;<a title=\"Tham khảo th&ecirc;m c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh bảng iPad ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo th&ecirc;m c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh bảng iPad ch&iacute;nh h&atilde;ng\">iPad</a>&nbsp;h&agrave;i h&ograve;a v&agrave; đẹp mắt hơn.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020.jpg\"><img class=\"lazy\" title=\"mặt trước iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020.jpg\" alt=\"mặt trước iPad Pro 12.9 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020.jpg\" /></a></p>\r\n<p>Tổng thể chiếc m&aacute;y c&oacute; trọng lượng chỉ 471 g v&agrave; mỏng 5.9 mm so với k&iacute;ch thước 12.9 inch th&igrave; điều đ&oacute; cho thấy iPad Pro 12.9 inch 2020 rất mỏng nhẹ, gọn g&agrave;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-4.jpg\"><img class=\"lazy\" title=\"Tr&ecirc;n tay k&iacute;ch thước iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-4.jpg\" alt=\"Tr&ecirc;n tay k&iacute;ch thước iPad Pro 12.9 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-4.jpg\" /></a></p>\r\n<p>Thật kh&oacute; l&ograve;ng khi t&igrave;m ra khuyết điểm về m&agrave;n h&igrave;nh của iPad Pro 12.9 inch 2020, với k&iacute;ch thước 12.9 inch c&ugrave;ng với độ ph&acirc;n giải 2048 x 2732 pixels gi&uacute;p cho m&aacute;y hiển thị v&ocirc; c&ugrave;ng sắc n&eacute;t v&agrave; kh&ocirc;ng gian l&agrave;m việc lớn.&nbsp;</p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"Độ ph&acirc;n giải m&agrave;n h&igrave;nh iPad Pro 12.9 2020\" href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cac-loai-do-phan-giai-man-hinh-1196142\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu độ ph&acirc;n giải m&agrave;n h&igrave;nh iPad Pro 12.9 2020\">T&igrave;m hiểu về c&aacute;c loại độ ph&acirc;n giải m&agrave;n h&igrave;nh</a></p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-6.jpg\"><img class=\"lazy\" title=\"k&iacute;ch thước m&agrave;n h&igrave;nh iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-6.jpg\" alt=\"k&iacute;ch thước m&agrave;n h&igrave;nh iPad Pro 12.9 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-6.jpg\" /></a></p>\r\n<p>M&aacute;y sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh Liquid Retina Display độ s&aacute;ng 600 nits với c&aacute;c c&ocirc;ng nghệ hỗ trợ như Pro Motion v&agrave; True Tone, gi&uacute;p m&agrave;u sắc được thể hiện một c&aacute;ch tự nhi&ecirc;n, trung thực v&agrave; sống động.</p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"m&agrave;n h&igrave;nh retina tr&ecirc;n iPad Pro 12.9 2020\" href=\"https://www.thegioididong.com/hoi-dap/man-hinh-retina-la-gi-905780\" target=\"_blank\" rel=\"noopener\" type=\"m&agrave;n h&igrave;nh retina tr&ecirc;n iPad Pro 12.9 2020\">M&agrave;n h&igrave;nh Retina l&agrave; g&igrave;?</a></p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-8.jpg\"><img class=\"lazy\" title=\"m&agrave;n h&igrave;nh hiển thị tr&ecirc;n iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-8.jpg\" alt=\"m&agrave;n h&igrave;nh hiển thị tr&ecirc;n iPad Pro 12.9 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-8.jpg\" /></a></p>\r\n<h3>Hiệu năng mạnh mẽ với chip CPU A12Z với 8 nh&acirc;n đồ họa</h3>\r\n<p>iPad Pro 12.9 inch 2020 được trang bị bộ vi xử l&yacute; Apple A12Z Bionic mạnh mẽ hơn người tiền nhiệm, gi&uacute;p cho thao t&aacute;c sử dụng những phần mềm đồ họa như Photoshop, illustrator mượt m&agrave; v&agrave; phản hồi nhanh ch&oacute;ng hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-5.jpg\"><img class=\"lazy\" title=\"Cấu h&igrave;nh iPad Pro 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-5.jpg\" alt=\"Cấu h&igrave;nh iPad Pro 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-5.jpg\" /></a></p>\r\n<p>Với việc c&oacute; thể kết nối với b&agrave;n ph&iacute;m, chuột kh&ocirc;ng d&acirc;y v&agrave; con trỏ chuột được l&agrave;m mới, gi&uacute;p người d&ugrave;ng thao t&aacute;c v&agrave; sử dụng một c&aacute;ch dễ d&agrave;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-3.jpg\"><img class=\"lazy\" title=\"iPad Pro 12.9 2020 kết nối b&agrave;n ph&iacute;m\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-3.jpg\" alt=\"iPad Pro 12.9 2020 kết nối b&agrave;n ph&iacute;m\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-3.jpg\" /></a></p>\r\n<p>Hơn thế nữa, bộ vi xử l&yacute; A12Z với 8 nh&acirc;n đồ hoạ th&igrave; m&aacute;y c&oacute; thể chiến hầu hết c&aacute;c tựa game hiện c&oacute; tr&ecirc;n thị trường như PUBG, Li&ecirc;n Qu&acirc;n, Asphalt 9,... với mức thiết lập đồ họa cao nhất với tốc độ khung h&igrave;nh ổn định trong suốt qu&aacute; tr&igrave;nh chơi.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-7.jpg\"><img class=\"lazy\" title=\"chơi game với iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-7.jpg\" alt=\"chơi game với iPad Pro 12.9 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-7.jpg\" /></a></p>\r\n<p>Bằng việc t&iacute;ch hợp sẵn bộ nhớ 128 GB gi&uacute;p cho người d&ugrave;ng c&oacute; nhiều kh&ocirc;ng gian lưu trữ hơn, l&agrave;m được nhiều việc hơn tr&ecirc;n chiếc iPad Pro 12.9 inch 2020. Đ&acirc;y l&agrave; mức dung lượng ho&agrave;n hảo cho t&ugrave;y chọn cơ bản nhất của chiếc m&aacute;y.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-9.jpg\"><img class=\"lazy\" title=\"Bộ nhớ tr&ecirc;n iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-9.jpg\" alt=\"Bộ nhớ tr&ecirc;n iPad Pro 12.9 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-9.jpg\" /></a></p>\r\n<h3>Cụm camera \"Pro\", n&acirc;ng tầm trải nghiệm AR</h3>\r\n<p>Apple thật sự ưu &aacute;i khi t&iacute;ch hợp cho chiếc m&aacute;y n&agrave;y với 2 camera sau, 1 camera ch&iacute;nh 12 MP v&agrave; 1 camera g&oacute;c si&ecirc;u rộng 125 độ 10 MP, c&ugrave;ng với đ&oacute; l&agrave; camera selfie 7 MP hỗ trợ c&ocirc;ng nghệ TrueDepth. Gi&uacute;p bạn ho&agrave;n to&agrave;n c&oacute; thể chụp ra những bức ảnh ưng &yacute; nhất.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-10.jpg\"><img class=\"lazy\" title=\"Cụm camera tr&ecirc;n iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-10.jpg\" alt=\"Cụm camera tr&ecirc;n iPad Pro 12.9 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-10.jpg\" /></a></p>\r\n<p>Việc quay video, chụp ảnh v&agrave; chỉnh sửa trực tiếp một c&aacute;ch chuy&ecirc;n nghiệp để gửi đi kh&aacute;ch h&agrave;ng, đối t&aacute;c chỉ với một thiết bị duy nhất đ&atilde; kh&ocirc;ng c&ograve;n l&agrave; điều xa vời với người d&ugrave;ng&nbsp;<a title=\"Tham khảo m&aacute;y t&iacute;nh bảng iPad Pro ch&iacute;nh h&atilde;ng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-pro\" target=\"_blank\" rel=\"noopener\">iPad Pro</a>&nbsp;2020.</p>\r\n<p>Hơn thế nữa, iPad Pro 12.9 inch 2020 c&ograve;n được t&iacute;ch hợp th&ecirc;m cảm biến Lidar đo chiều s&acirc;u, gi&uacute;p hỗ trợ những ứng dụng AR nhận diện bối cảnh một c&aacute;ch ch&iacute;nh x&aacute;c nhất.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-11.jpg\"><img class=\"lazy\" title=\"Lidar hỗ trợ AR tr&ecirc;n iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-11.jpg\" alt=\"Lidar hỗ trợ AR tr&ecirc;n iPad Pro 12.9 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-11.jpg\" /></a></p>\r\n<p>Cảm biến n&agrave;y cực k&igrave; hữu &iacute;ch đặc biệt trong c&aacute;c lĩnh vực thiết kế, thi c&ocirc;ng bởi người d&ugrave;ng c&oacute; thể \"ướm\" thử c&aacute;c m&ocirc; h&igrave;nh 3D của thiết kế l&ecirc;n thực tế v&agrave; quan s&aacute;t nhiều g&oacute;c độ một c&aacute;ch trực quan nhất bằng c&ocirc;ng nghệ thực tại ảo AR.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020.gif\"><img class=\"lazy\" title=\"Cảm biến Lidar hỗ trợ AR mượt m&agrave; tr&ecirc;n iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020.gif\" alt=\"Cảm biến Lidar hỗ trợ AR mượt m&agrave; tr&ecirc;n iPad Pro 12.9 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020.gif\" /></a></p>\r\n<p>Nh&igrave;n chung,&nbsp;<a title=\"Tham khảo c&aacute;c sản phẩm ch&iacute;nh h&atilde;ng của Apple tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/apple\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; điện thoại smartphone iPhone ch&iacute;nh h&atilde;ng\">Apple</a>&nbsp;đ&atilde; định vị iPad rất r&otilde; r&agrave;ng hướng tới việc ng&agrave;y c&agrave;ng mang lại nhiều trải nghiệm l&agrave;m việc, giải tr&iacute; mạnh mẽ hơn cho người d&ugrave;ng chuy&ecirc;n nghiệp. Ở hiện tại, iPad Pro 2020 chắc chắn l&agrave; thiết bị rất đ&aacute;ng xuống tiền để sử dụng như một thiết bị l&agrave;m việc hỗ trợ để xử l&yacute; c&aacute;c c&ocirc;ng việc gấp khi du lịch, ra ngo&agrave;i hay l&agrave; c&ocirc;ng cụ rất \"ngầu\" d&ugrave;ng để giao tiếp với đối t&aacute;c.</p>', 27190000, 'uploads/products/ipad-pro-12-9-inch-wifi-128gb-2020-bac-600x600-1-600x600.jpg', 45, '1', '2021-10-18 12:59:50', '2021-10-18 12:59:50', 12, NULL),
(27, 'iPad Pro 11 inch Wifi 128GB (2020)', '<h2><a title=\"Tham khảo gi&aacute; m&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 ch&iacute;nh h&atilde;ng tại thegioididong.com\" href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-11-inch-2020\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; m&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 ch&iacute;nh h&atilde;ng tại thegioididong.com\">iPad Pro 11 inch 2020</a>&nbsp;l&agrave; mẫu&nbsp;<a title=\"Tham khảo gi&aacute; c&aacute;c mẫu m&aacute;y t&iacute;nh bảng Apple iPad ch&iacute;nh h&atilde;ng tại thegioididong.com\" href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; c&aacute;c mẫu m&aacute;y t&iacute;nh bảng Apple iPad ch&iacute;nh h&atilde;ng tại thegioididong.com\">iPad</a>&nbsp;mới nhất vừa được&nbsp;<a title=\"Tham khảo sản phẩm Apple ch&iacute;nh h&atilde;ng tại Thegioididong.com \" href=\"https://www.thegioididong.com/apple\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; điện thoại smartphone iPhone ch&iacute;nh h&atilde;ng\">Apple</a>&nbsp;ra mắt v&agrave;o 18/3 với thiết kế gần như kh&ocirc;ng thay đổi so với thế hệ trước, chủ yếu l&agrave; sự n&acirc;ng cấp đến từ chip A12Z cho hiệu năng mạnh mẽ v&agrave; cụm camera c&oacute; cảm biến mới hỗ trợ tăng cường thực tế ảo.</h2>', '<h3>Cấu h&igrave;nh khủng vượt giới hạn của một chiếc tablet</h3>\r\n<p>Được Apple trang bị vi xử l&yacute; A12Z Bionic mới nhất, iPad Pro 11 inch c&oacute; một tốc độ vượt trội, c&oacute; thể so s&aacute;nh được với c&aacute;c mẫu&nbsp;<a title=\"Tham khảo gi&aacute; laptop m&aacute;y t&iacute;nh x&aacute;ch tay ch&iacute;nh h&atilde;ng tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; laptop m&aacute;y t&iacute;nh x&aacute;ch tay ch&iacute;nh h&atilde;ng tại thegioididong.com\">m&aacute;y t&iacute;nh x&aacute;ch tay</a>&nbsp;hiện tại.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-1.jpg\"><img class=\"lazy\" title=\"M&aacute;y t&iacute;nh bảng iPad Pro 2020 c&oacute; một cấu h&igrave;nh khủng gần như tương đương PC\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-1.jpg\" alt=\"M&aacute;y t&iacute;nh bảng iPad Pro 2020 c&oacute; một cấu h&igrave;nh khủng gần như tương đương PC\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-1.jpg\" /></a></p>\r\n<p>Mọi ứng dụng hoạt động tr&ecirc;n iPad trước vốn đ&atilde; mượt, nay c&ograve;n nhanh v&agrave; tr&ocirc;i chảy hơn, từ&nbsp;c&aacute;c t&aacute;c vụ đơn giản h&agrave;ng ng&agrave;y cho đến c&aacute;c c&ocirc;ng việc chuy&ecirc;n s&acirc;u về đồ họa đều được xử l&yacute; nhanh ch&oacute;ng.</p>\r\n<p>Apple cũng đ&atilde; tăng cường đến 8 l&otilde;i&nbsp;xử l&yacute; đồ họa, gi&uacute;p cho iPad Pro 2020 trở th&agrave;nh một chiếc&nbsp;<a title=\"Tham khảo m&aacute;y t&iacute;nh bảng ch&iacute;nh h&atilde;ng gi&aacute; tốt tại thegioididong.com\" href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo m&aacute;y t&iacute;nh bảng ch&iacute;nh h&atilde;ng gi&aacute; tốt tại thegioididong.com\">m&aacute;y t&iacute;nh bảng</a>&nbsp;đ&aacute;p ứng tuyệt vời cho nhu cầu đồ họa, giải tr&iacute; v&agrave; chơi game.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-3.jpg\"><img class=\"lazy\" title=\"M&aacute;y t&iacute;nh bảng Apple iPad Pro 11 inch 2020 sở hữu hiệu năng mạnh mẽ\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-3.jpg\" alt=\"M&aacute;y t&iacute;nh bảng Apple iPad Pro 11 inch 2020 sở hữu hiệu năng mạnh mẽ\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-3.jpg\" /></a></p>\r\n<p>iPad&nbsp;Pro 11 inch 2020 vẫn duy tr&igrave; sử dụng m&agrave;n h&igrave;nh Liquid Retina c&ugrave;ng c&aacute;c t&iacute;nh năng h&agrave;ng đầu như ProMotion, tần số qu&eacute;t l&ecirc;n đến 120 Hz, True Tone, gi&uacute;p cho iPad Pro trở th&agrave;nh một trong những thiết bị di động sở hữu m&agrave;n h&igrave;nh tốt nhất hiện nay (3/2020).</p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"T&igrave;m hiểu về c&ocirc;ng nghệ m&agrave;n h&igrave;nh Liquid Retina của Apple\" href=\"https://www.thegioididong.com/hoi-dap/cung-tim-hieu-ve-man-hinh-liquid-retina-tren-iphon-1125106\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu về c&ocirc;ng nghệ m&agrave;n h&igrave;nh Liquid Retina của Apple\">C&ugrave;ng t&igrave;m hiểu về m&agrave;n h&igrave;nh Liquid Retina tr&ecirc;n iPhone XR</a></p>\r\n<p>Chất lượng hiển thị tr&ecirc;n iPad Pro 2020 l&agrave; cực kỳ xuất sắc, dải m&agrave;u rộng hiển thị chuẩn x&aacute;c nhằm tối ưu cho việc chỉnh sửa đồ họa chuy&ecirc;n nghiệp, độ s&aacute;ng cao 600 nit đ&atilde; gi&uacute;p bạn c&oacute; thể sử dụng m&aacute;y ngo&agrave;i trời một c&aacute;ch dễ d&agrave;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-2020-1.jpg\"><img class=\"lazy\" title=\"M&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 | M&agrave;n h&igrave;nh Liquid Retina\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-1.jpg\" alt=\"M&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 | M&agrave;n h&igrave;nh Liquid Retina\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-1.jpg\" /></a></p>\r\n<h3>Camera đa t&iacute;nh năng, hỗ trợ AR tốt hơn</h3>\r\n<p>Sự thay đổi&nbsp;về ngoại h&igrave;nh duy nhất của&nbsp;iPad Pro 2020 c&oacute; lẽ l&agrave; cụm camera sau h&igrave;nh vu&ocirc;ng ở mặt lưng với thiết kế tương tự d&ograve;ng iPhone 11. M&ocirc;-đun camera n&agrave;y chứa 3 cảm biến, gồm camera ch&iacute;nh g&oacute;c rộng 12 MP v&agrave; camera g&oacute;c si&ecirc;u rộng 10 MP v&agrave; cảm biến mới c&oacute; t&ecirc;n&nbsp;LiDAR.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-2020-2.jpg\"><img class=\"lazy\" title=\"M&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 với cụm camera sau h&igrave;nh vu&ocirc;ng\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-2.jpg\" alt=\"M&aacute;y t&iacute;nh bảng iPad Pro 11 inch 2020 với cụm camera sau h&igrave;nh vu&ocirc;ng\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-2.jpg\" /></a></p>\r\n<p>Camera ch&iacute;nh 12 MP hỗ trợ chụp những tấm ảnh thường ng&agrave;y r&otilde; n&eacute;t. Trong khi đ&oacute;, ống k&iacute;nh Ultra Wide 10 MP tăng cường độ mở cho g&oacute;c chụp si&ecirc;u rộng ấn tượng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-7.jpg\"><img class=\"lazy\" title=\"Chụp h&igrave;nh v&agrave; chỉnh sửa nhanh ch&oacute;ng với M&aacute;y t&iacute;nh bảng iPad Pro 2020 11 inch\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-7.jpg\" alt=\"Chụp h&igrave;nh v&agrave; chỉnh sửa nhanh ch&oacute;ng với M&aacute;y t&iacute;nh bảng iPad Pro 2020 11 inch\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-7.jpg\" /></a></p>\r\n<p>Với giao diện camera tối giản, dễ d&ugrave;ng tr&ecirc;n iPad, bạn c&oacute; thể chụp to&agrave;n bộ căn ph&ograve;ng trong một lần chụp bằng camera Ultra Wide v&agrave; chuyển đến ứng dụng để thiết kế ngay v&agrave; lưu lại t&aacute;c phẩm của m&igrave;nh với những thao t&aacute;c cực kỳ đơn giản, nhanh ch&oacute;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-inch-2020.gif\"><img class=\"lazy\" title=\"M&aacute;y t&iacute;nh bảng iPad Pro 2020 11 inch | Chế độ VR mượt m&agrave; nhờ cảm biến LiDAR\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-inch-2020.gif\" alt=\"M&aacute;y t&iacute;nh bảng iPad Pro 2020 11 inch | Chế độ VR mượt m&agrave; nhờ cảm biến LiDAR\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-inch-2020.gif\" /></a></p>\r\n<p>Hệ thống camera&nbsp;<a title=\"Tham khảo iPad Pro ch&iacute;nh h&atilde;ng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-pro\" target=\"_blank\" rel=\"noopener\">iPad Pro</a>&nbsp;vận h&agrave;nh linh hoạt đến kh&ocirc;ng ngờ. Từ việc quay, chỉnh sửa cho đến chia sẻ video 4K, tất cả thật dễ d&agrave;ng tr&ecirc;n c&ugrave;ng một thiết bị. Bạn cũng c&oacute; thể quay video ho&agrave;n hảo với micro đạt chuẩn ph&ograve;ng thu, ph&aacute;t &acirc;m thanh 4 k&ecirc;nh, l&agrave;m cho phần nghe của thước phim trở n&ecirc;n sống động hơn.</p>\r\n<p>Cảm biến LiDAR (Light Detection and Ranging) l&agrave; một c&ocirc;ng nghệ h&igrave;nh ảnh mới, lần đầu ti&ecirc;n được Apple t&iacute;ch hợp tr&ecirc;n thiết bị của m&igrave;nh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-8.jpg\"><img class=\"lazy\" title=\"Cảm biến LiDAR tr&ecirc;n iPad Pro 11 inch 2020 hỗ trợ thực tế ảo mạnh mẽ hơn\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-8.jpg\" alt=\"Cảm biến LiDAR tr&ecirc;n iPad Pro 11 inch 2020 hỗ trợ thực tế ảo mạnh mẽ hơn\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-8.jpg\" /></a></p>\r\n<p>Cảm biến n&agrave;y hoạt động dựa tr&ecirc;n nguy&ecirc;n l&yacute; thời gian phản xạ của &aacute;nh s&aacute;ng đến vật thể để phục vụ đo chiều s&acirc;u, t&iacute;nh to&aacute;n khoảng c&aacute;ch một c&aacute;ch ch&iacute;nh x&aacute;c hỗ trợ đắc lực cho hoạt động của c&aacute;c ứng dụng AR.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-2020-3.jpg\"><img class=\"lazy\" title=\"Camera TrueDepth ở mặt trước m&aacute;y t&iacute;nh bảng iPad Pro 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-3.jpg\" alt=\"Camera TrueDepth ở mặt trước m&aacute;y t&iacute;nh bảng iPad Pro 2020\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-3.jpg\" /></a></p>\r\n<p>Camera TrueDepth ở mặt trước sẽ hỗ trợ nhận diện khu&ocirc;n mặt 3D Face ID,&nbsp;chụp ảnh ch&acirc;n dung. Tr&ograve; chuyện với bạn b&egrave; qua FaceTime sẽ trở n&ecirc;n th&uacute; vị hơn với&nbsp;t&iacute;nh năng Animoji vui nhộn.</p>\r\n<h3>Thiết kế quen thuộc, vẫn bền bỉ v&agrave; đẳng cấp</h3>', 21190000, 'uploads/products/ipad-pro-11-inch-2020-bac-600x600-1-600x600.jpg', 34, '1', '2021-10-18 13:01:39', '2021-10-18 13:01:39', 12, NULL),
(28, 'iPad Huawei MatePad T8 (Nền tảng Huawei Mobile Service)', '<h2><a title=\"M&aacute;y t&iacute;nh bảng Huawei MatePad T8 tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang/huawei-matepad-t8\" target=\"_blank\" rel=\"noopener\" type=\"M&aacute;y t&iacute;nh bảng Huawei MatePad T8 tại Thế Giới Di Động\">Huawei MatePad T8</a>&nbsp;l&agrave; một trong những mẫu&nbsp;<a title=\"Tham khảo m&aacute;y t&iacute;nh bảng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\">m&aacute;y t&iacute;nh bảng</a>&nbsp;gi&aacute; rẻ của&nbsp;Huawei&nbsp;c&oacute; thiết kế nguy&ecirc;n khối c&ugrave;ng một cấu h&igrave;nh cơ bản, đủ d&ugrave;ng cho c&aacute;c t&aacute;c vụ hằng ng&agrave;y của mọi người d&ugrave;ng.</h2>', '<h3>Thiết kế nguy&ecirc;n khối liền mạch, m&agrave;n h&igrave;nh viền mỏng</h3>\r\n<p><a title=\"M&aacute;y t&iacute;nh bản Huawei tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang-huawei\" target=\"_blank\" rel=\"noopener\" type=\"M&aacute;y t&iacute;nh bản Huawei tại Thế Giới Di Động\">M&aacute;y t&iacute;nh bảng Huawei</a>&nbsp;MatePad T8 mang cho m&igrave;nh thiết kế chắc chắn, viền m&agrave;n h&igrave;nh 2 b&ecirc;n mỏng tạo cho trải nghiệm xem rộng r&atilde;i v&agrave; cảm gi&aacute;c cầm nắm tốt.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/223131/huawei-matepad-t8-tgdd3.jpg\"><img class=\"lazy\" title=\"M&agrave;n h&igrave;nh giải tr&iacute; lớn của Huawei MatePad T8\" src=\"https://cdn.tgdd.vn/Products/Images/522/223131/huawei-matepad-t8-tgdd3.jpg\" alt=\"M&agrave;n h&igrave;nh giải tr&iacute; lớn của Huawei MatePad T8\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/223131/huawei-matepad-t8-tgdd3.jpg\" /></a></p>\r\n<p>M&aacute;y được trang bị&nbsp;m&agrave;n h&igrave;nh&nbsp;<a title=\"M&aacute;y t&iacute;nh bảng c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh 8 inch tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang?g=khoang-8\" target=\"_blank\" rel=\"noopener\" type=\"M&aacute;y t&iacute;nh bảng c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh 8 inch tại Thế Giới Di Động\">k&iacute;ch thước 8 inch</a>&nbsp;với độ ph&acirc;n giải HD 1280x800 pixel, tuy m&agrave;n h&igrave;nh c&oacute; độ ph&acirc;n giải kh&ocirc;ng cao, nhưng vẫn đảm bảo chất lượng hiển thị tốt, m&agrave;u sắc r&otilde; r&agrave;ng nhờ v&agrave;o&nbsp;<a title=\"M&agrave;n h&igrave;nh IPS LCD l&agrave; g&igrave;?\" href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\" rel=\"noopener\">c&ocirc;ng nghệ m&agrave;n h&igrave;nh IPS</a>.</p>\r\n<h3>Cấu h&igrave;nh kh&aacute; tốt trong tầm gi&aacute;</h3>\r\n<p>Huawei MatePad T8 sở hữu vi xử l&yacute; MediaTek MT8768 8 nh&acirc;n, RAM 2 GB gi&uacute;p chiếc&nbsp;<a title=\"Tham khảo m&aacute;y t&iacute;nh bảng ch&iacute;nh h&atilde;ng, gi&aacute; rẻ tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo m&aacute;y t&iacute;nh bảng ch&iacute;nh h&atilde;ng, gi&aacute; rẻ tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng</a>&nbsp;n&agrave;y c&oacute; thể đ&aacute;p ứng tốt c&aacute;c nhu cầu cơ bản như lướt web, xem phim hay chơi một số c&aacute;c tựa game nhẹ.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/223131/huawei-matepad-t8-292020-102043.jpg\"><img class=\"lazy\" title=\"Thiết kế tr&agrave;n viền của m&aacute;y t&iacute;nh bảng Huawei MatePad T8\" src=\"https://cdn.tgdd.vn/Products/Images/522/223131/huawei-matepad-t8-292020-102043.jpg\" alt=\"Thiết kế tr&agrave;n viền của m&aacute;y t&iacute;nh bảng Huawei MatePad T8\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/223131/huawei-matepad-t8-292020-102043.jpg\" /></a></p>\r\n<p>M&aacute;y c&oacute; bộ nhớ trong 32 GB, tuy bộ nhớ kh&ocirc;ng qu&aacute; nhiều nhưng vẫn đủ để bạn c&oacute; thể lưu trữ hay tải nhiều ứng dụng. Nếu bạn muốn c&oacute; th&ecirc;m kh&ocirc;ng gian lưu trữ th&igrave; m&aacute;y vẫn c&oacute; hỗ trợ&nbsp;<a title=\"Tham khảo thẻ nhớ kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/the-nho-dien-thoai\" target=\"_blank\" rel=\"noopener\">thẻ nhớ</a>&nbsp;ngo&agrave;i Micro SD dung lượng 512 GB, gi&uacute;p bạn kh&ocirc;ng phải lo lắng về khả năng lưu trữ của m&aacute;y.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/223131/huawei-matepad-t8-tgdd4.jpg\"><img class=\"lazy\" title=\"Thời lượng pin lớn của Huawei MatePad T8\" src=\"https://cdn.tgdd.vn/Products/Images/522/223131/huawei-matepad-t8-tgdd4.jpg\" alt=\"Thời lượng pin lớn của Huawei MatePad T8\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/223131/huawei-matepad-t8-tgdd4.jpg\" /></a></p>\r\n<p>Thời lượng&nbsp;<a title=\"Tham khảo pin c&aacute;c loại kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/pin\" target=\"_blank\" rel=\"noopener\">pin</a>&nbsp;của m&aacute;y rất ấn tượng khi được trang bị vi&ecirc;n pin 5100 mAh, cho thời gian sử dụng cả một ng&agrave;y d&agrave;i chỉ với 1 lần sạc.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/223131/huawei-matepad-t8-tgdd7.jpg\"><img class=\"lazy\" title=\"Cổng sạc của Huawei MatePad T8\" src=\"https://cdn.tgdd.vn/Products/Images/522/223131/huawei-matepad-t8-tgdd7.jpg\" alt=\"Cổng sạc của Huawei MatePad T8\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/223131/huawei-matepad-t8-tgdd7.jpg\" /></a></p>\r\n<p>Hơn thế nữa, m&aacute;y c&ograve;n hỗ trợ Nano SIM kết nối 4G gi&uacute;p kết nối internet dễ d&agrave;ng khi kh&ocirc;ng c&oacute; Wi-Fi. Ngo&agrave;i ra Huawei MatePad T8 c&ograve;n hỗ trợ Bluetooth 5.0, kết nối OTG tiện lợi để c&oacute; thể kết hợp với&nbsp;<a title=\"Tham khảo b&agrave;n ph&iacute;m kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/ban-phim\" target=\"_blank\" rel=\"noopener\">b&agrave;n ph&iacute;m</a>,&nbsp;<a title=\"Tham khảo chuột m&aacute;y t&iacute;nh kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/chuot-may-tinh\" target=\"_blank\" rel=\"noopener\">chuột</a>&nbsp;hay c&aacute;c phụ kiện hỗ trợ kh&aacute;c.</p>\r\n<p>Tuy MatePad T8 kh&ocirc;ng hỗ trợ Google, nhưng bạn c&oacute; thể tải ứng dụng qua HUAWEI APPGallery c&oacute; sẵn tr&ecirc;n m&aacute;y với kho ứng dụng đa dạng, phong ph&uacute;.</p>', 3090000, 'uploads/products/huawei-matepad-t8-xanh-600x600.jpg', 54, '1', '2021-10-18 13:03:05', '2021-10-18 13:03:05', 12, NULL);
INSERT INTO `products` (`id`, `name`, `desc`, `content`, `price`, `thumbnail`, `qty`, `status`, `created_at`, `updated_at`, `cat_id`, `deleted_at`) VALUES
(29, 'ipad Huawei MatePad T10s (Nền tảng Huawei Mobile Service)', '<h3>Chiếc&nbsp;<a title=\"Tham khảo c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh bảng gi&aacute; tốt, ch&iacute;nh h&atilde;ng đang kinh doanh tại Thegioididong.com\" href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh bảng gi&aacute; tốt, ch&iacute;nh h&atilde;ng đang kinh doanh tại Thegioididong.com\">m&aacute;y t&iacute;nh bảng</a>&nbsp;gi&aacute; rẻ đ&aacute;ng mong chờ của&nbsp;<a title=\"Tham khảo c&aacute;c mẫu m&aacute;y t&iacute;nh bảng Huawei gi&aacute; tốt tại Thegioididong.com\" href=\"https://www.thegioididong.com/may-tinh-bang-huawei\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c mẫu m&aacute;y t&iacute;nh bảng Huawei gi&aacute; tốt tại Thegioididong.com\">Huawei</a>,&nbsp;<a title=\"Tham khảo m&aacute;y t&iacute;nh bảng Huawei MatePad T10s tại Thegioididong.com \" href=\"https://www.thegioididong.com/may-tinh-bang/huawei-matepad-t10s\" target=\"_blank\" rel=\"noopener\">Huawei MatePad T10s</a>&nbsp;cuối c&ugrave;ng cũng đ&atilde; ch&iacute;nh thức ra mắt. Với vi xử l&yacute; 8 nh&acirc;n mở ra một thế giới giải tr&iacute; mượt m&agrave;, sống động từng khoảnh khắc với m&agrave;n h&igrave;nh cực lớn, h&eacute; lộ một chiếc m&aacute;y t&iacute;nh bảng tốt trong tầm gi&aacute; m&agrave; bất kỳ ai cũng đều y&ecirc;u th&iacute;ch.</h3>', '<h3>Giải tr&iacute; sống động với m&agrave;n h&igrave;nh cực lớn</h3>\r\n<p>MatePad T10s sở hữu&nbsp;<a title=\"Tham khảo c&aacute;c mẫu m&aacute;y t&iacute;nh bảng c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh từ 10 đến 11 inch tại Thegioididong.com\" href=\"https://www.thegioididong.com/may-tinh-bang-khoang-10-den-11-inch\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c mẫu m&aacute;y t&iacute;nh bảng c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh từ 10 đến 11 inch tại Thegioididong.com\">m&agrave;n h&igrave;nh 10.1 inch</a>&nbsp;tr&agrave;n đều c&acirc;n xứng ho&agrave;n hảo ở cả 4 cạnh viền. Gi&uacute;p cho thiết bị c&oacute; một thiết kế gọn, nhẹ v&agrave; mỏng hơn, từng giờ ph&uacute;t trải nghiệm l&agrave; đều l&agrave; sự thoải m&aacute;i.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/233257/huawei-matepad-t10s-181121-031133.jpg\"><img class=\"lazy\" title=\"M&agrave;n h&igrave;nh mỏng, cầm nhẹ tr&ecirc;n tay | Huawei MatePad T10s\" src=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-181121-031133.jpg\" alt=\"M&agrave;n h&igrave;nh mỏng, cầm nhẹ tr&ecirc;n tay | Huawei MatePad T10s\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-181121-031133.jpg\" /></a></p>\r\n<p>Sử dụng tấm nền IPS LCD c&oacute; độ ph&acirc;n giải 1200 x 1920 pixels cho bạn tận hưởng c&aacute;c nội dung giải tr&iacute; lớn hơn, r&otilde; r&agrave;ng hơn với độ sắc n&eacute;t tuyệt vời, nội dung sẽ linh hoạt thay đổi mượt m&agrave; theo chiều cầm ngang hay dọc.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/233257/huawei-matepad-t10s-180921-030921.jpg\"><img class=\"lazy\" title=\"M&agrave;n h&igrave;nh lớn sắc n&eacute;t | Huawei MatePad T10s\" src=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-180921-030921.jpg\" alt=\"M&agrave;n h&igrave;nh lớn sắc n&eacute;t | Huawei MatePad T10s\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-180921-030921.jpg\" /></a></p>\r\n<p>C&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh HUAWEI ClariVu độc quyền gi&uacute;p tăng cường chất lượng h&igrave;nh ảnh v&agrave; c&acirc;n bằng m&agrave;u sắc trong nhiều điều kiện kh&aacute;c nhau mang đến trải nghiệm giải tr&iacute; ấn tượng hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-20-800x533.jpg\"><img class=\"lazy\" title=\"Thay đổi m&agrave;u m&agrave;n h&igrave;nh ph&ugrave; hợp cho mắt | Huawei MatePad T10s\" src=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-20-800x533.jpg\" alt=\"Thay đổi m&agrave;u m&agrave;n h&igrave;nh ph&ugrave; hợp cho mắt | Huawei MatePad T10s\" data-original=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-20-800x533.jpg\" /></a></p>\r\n<p>M&agrave;n h&igrave;nh MatePad T10s đạt chứng nhận bảo vệ mắt T&Uuml;V Rheinland giảm lượng &aacute;nh s&aacute;ng xanh c&oacute; hại, giữ cho đ&ocirc;i mắt của bạn lu&ocirc;n dễ chịu khi sử dụng trong nhiều giờ liền.</p>\r\n<h3>Loa k&eacute;p Harman Kardon cho chất &acirc;m v&ograve;m ấn tượng</h3>\r\n<p>Kh&ocirc;ng chỉ c&oacute; phần nh&igrave;n ấn tượng, m&agrave; phần nghe tr&ecirc;n MatePad T10s cũng được đ&aacute;nh gi&aacute; cao khi trang bị bộ đ&ocirc;i loa ngo&agrave;i đối xứng, giả lập hiệu ứng &acirc;m thanh v&ograve;m 3D Histen 6.1 Sound n&acirc;ng tầm trải nghiệm mỗi khi chơi game hay xem phim.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-1-800x533.jpg\"><img class=\"lazy\" title=\"Hỗ trợ &acirc;m thanh v&ograve;m | Huawei MatePad T10s\" src=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-1-800x533.jpg\" alt=\"Hỗ trợ &acirc;m thanh v&ograve;m | Huawei MatePad T10s\" data-original=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-1-800x533.jpg\" /></a></p>\r\n<p>Chất &acirc;m của T10s đ&atilde; được tinh chỉnh bởi Harman Kardon gi&uacute;p &acirc;m thanh trở n&ecirc;n r&otilde; r&agrave;ng, h&agrave;i h&ograve;a tr&ecirc;n mọi &acirc;m vực, thể hiện đ&uacute;ng sắc th&aacute;i đặc trưng của b&agrave;i h&aacute;t.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/233257/huawei-matepad-t10s-180921-030951.jpg\"><img class=\"lazy\" title=\"&Acirc;m thanh tinh chỉnh bới Harman Kardon | Huawei MatePad T10s\" src=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-180921-030951.jpg\" alt=\"&Acirc;m thanh tinh chỉnh bới Harman Kardon | Huawei MatePad T10s\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-180921-030951.jpg\" /></a></p>\r\n<h3>Vi xử l&yacute; 8 nh&acirc;n nhanh v&agrave; si&ecirc;u ổn định</h3>\r\n<p>Trải nghiệm sự mượt m&agrave; đến từ vi xử l&yacute; 8 nh&acirc;n Kirin 710A, mang đến hiệu năng vượt trội trong tầm gi&aacute;, lướt chạm mượt m&agrave;, ứng dụng k&iacute;ch hoạt nhanh ch&oacute;ng, thỏa sức tận hưởng thế giới giải tr&iacute; th&uacute; vị đến từ kho ứng dụng AppGallery của Huawei.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-16-800x533.jpg\"><img class=\"lazy\" title=\"Giải tr&iacute; ổn định | Huawei MatePad T10s\" src=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-16-800x533.jpg\" alt=\"Giải tr&iacute; ổn định | Huawei MatePad T10s\" data-original=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-16-800x533.jpg\" /></a></p>\r\n<p>B&ecirc;n cạnh đ&oacute;, trang bị&nbsp;<a title=\"Tham khảo c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh bảng c&oacute; dung lượng RAM từ 3 GB đến 4 GB tại Thegioididong.com\" href=\"https://www.thegioididong.com/may-tinh-bang-ram-duoi-4gb\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh bảng c&oacute; dung lượng RAM từ 3 GB đến 4 GB tại Thegioididong.com\">3 GB RAM</a>&nbsp;cho đa nhiệm ổn định v&agrave;&nbsp;<a title=\"Tham khảo c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh bảng c&oacute; bộ nhớ trong 32 GB - 64 GB cho nhu cầu lưu trữ cơ bản tại Thegioididong.com\" href=\"https://www.thegioididong.com/may-tinh-bang-rom-32-den-64gb\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh bảng c&oacute; bộ nhớ trong 32 GB - 64 GB cho nhu cầu lưu trữ cơ bản tại Thegioididong.com\">bộ nhớ trong 64 GB</a>, gi&uacute;p lưu trữ thoải m&aacute;i dữ liệu, ứng dụng, nội dung tải xuống, đồng thời mang đến sự ổn định khi mở c&ugrave;ng l&uacute;c 2 cửa sổ tr&ecirc;n một m&agrave;n h&igrave;nh, hay chuyển đổi ứng dụng mượt m&agrave; hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/233257/huawei-matepad-t10s-181021-031055.jpg\"><img class=\"lazy\" title=\"Hỗ trợ thẻ nhớ ngo&agrave;i MicroSD tối đa 256 GB | Huawei MatePad T10s\" src=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-181021-031055.jpg\" alt=\"Hỗ trợ thẻ nhớ ngo&agrave;i MicroSD tối đa 256 GB | Huawei MatePad T10s\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-181021-031055.jpg\" /></a></p>\r\n<h3>EMUI 10.1 đa nhiệm tốt hơn, hỗ trợ kh&ocirc;ng gian ri&ecirc;ng cho b&eacute;.</h3>\r\n<p>EMUI 10.1 hoạt động tr&ecirc;n Android 10 m&atilde; nguồn mở với nhiều t&iacute;nh năng hấp dẫn. T&iacute;nh năng HUAWEI App Multiplier tận dụng m&agrave;n h&igrave;nh lớn cho ph&eacute;p bạn mở c&ugrave;ng l&uacute;c 2 cửa sổ ứng dụng hoạt động ri&ecirc;ng biệt, l&agrave;m 2 việc c&ugrave;ng l&uacute;c gi&uacute;p cho c&ocirc;ng việc của bạn được xử l&yacute; nhanh ch&oacute;ng v&agrave; hiệu quả hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-3-800x533.jpg\"><img class=\"lazy\" title=\"Chạy đa nhiệm tốt | Huawei MatePad T10s\" src=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-3-800x533.jpg\" alt=\"Chạy đa nhiệm tốt | Huawei MatePad T10s\" data-original=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-3-800x533.jpg\" /></a></p>\r\n<p>MatePad T10s c&ograve;n l&agrave; chiếc m&aacute;y t&iacute;nh bảng được hội phụ huynh tin tưởng chọn lựa cho trẻ với Kids Corner, mở ra một thế giới trẻ thơ d&agrave;nh ri&ecirc;ng cho b&eacute;. M&agrave;n h&igrave;nh giảm &aacute;nh s&aacute;ng xanh 6 lớp với độ s&aacute;ng được căn chỉnh hợp l&yacute; bảo vệ mắt b&eacute; tốt hơn.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-10-800x533.jpg\"><img class=\"lazy\" title=\"C&oacute; ứng dụng cho trẻ em | Huawei MatePad T10s\" src=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-10-800x533.jpg\" alt=\"C&oacute; ứng dụng cho trẻ em | Huawei MatePad T10s\" data-original=\"https://cdn.tgdd.vn/2021/01/content/huawei-matepad-t10s-10-800x533.jpg\" /></a></p>\r\n<h3>Pin 5100 mAh giải tr&iacute;, l&agrave;m việc li&ecirc;n tục nhiều giờ liền</h3>\r\n<p>MatePad T10s được trang bị vi&ecirc;n pin lớn 5100 mAh thoải m&aacute;i cho bạn giải tr&iacute; hay l&agrave;m việc li&ecirc;n tục trong nhiều giờ liền. Ở bất kỳ kh&ocirc;ng gian v&agrave; thời gian n&agrave;o, MatePad T10s sẽ lu&ocirc;n sẵn s&agrave;ng gi&uacute;p bạn ho&agrave;n th&agrave;nh c&ocirc;ng việc nhanh ch&oacute;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/233257/huawei-matepad-t10s-181021-031042.jpg\"><img class=\"lazy\" title=\"Giải tr&iacute; suốt cả ng&agrave;y với vi&ecirc;n pin 5100 mAh | Huawei MatePad T10s\" src=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-181021-031042.jpg\" alt=\"Giải tr&iacute; suốt cả ng&agrave;y với vi&ecirc;n pin 5100 mAh | Huawei MatePad T10s\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-181021-031042.jpg\" /></a></p>\r\n<p>Kh&ocirc;ng chỉ vậy, MatePad T10s c&ograve;n c&oacute; đầy đủ cả camera trước sau. Camera sau 5 MP, hỗ trợ quay phim Full HD đủ để cho bạn ghi lại những kỉ niệm đ&aacute;ng nhớ b&ecirc;n gia đ&igrave;nh hay khoảnh khắc th&uacute; vị xung quanh bạn. Camera trước 2 MP sẽ hỗ trợ những cuộc gọi video call r&otilde; n&eacute;t hay tỏa s&aacute;ng với những tấm ảnh selfie thần th&aacute;i.</p>\r\n<p><a class=\"preventdefault\" href=\"https://www.thegioididong.com/images/522/233257/huawei-matepad-t10s-180921-030959.jpg\"><img class=\"lazy\" title=\"Camera sau tr&ecirc;n m&aacute;y | Huawei MatePad T10s\" src=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-180921-030959.jpg\" alt=\"Camera sau tr&ecirc;n m&aacute;y | Huawei MatePad T10s\" data-original=\"https://cdn.tgdd.vn/Products/Images/522/233257/huawei-matepad-t10s-180921-030959.jpg\" /></a></p>\r\n<p>T&oacute;m lại, MatePad T10s đ&aacute;p ứng tốt mọi nhu cầu bạn cần ở một chiếc m&aacute;y t&iacute;nh bảng từ giải tr&iacute; cho đến c&ocirc;ng việc. Với mức gi&aacute; rẻ dễ tiếp cận, MatePad T10s l&agrave; chiếc m&aacute;y t&iacute;nh bảng l&agrave;m ra để h&agrave;i l&ograve;ng tất cả mọi người từ học sinh, nh&acirc;n vi&ecirc;n văn ph&ograve;ng, trẻ em, người lớn tuổi đều sẽ y&ecirc;u th&iacute;ch mỗi khi sử dụng.</p>', 51900000, 'uploads/products/huawei-t10s-600x600-600x600.jpg', 45, '1', '2021-10-18 13:06:35', '2021-10-18 13:06:35', 12, NULL);

